// 🎯 Опростен админ панел с base64 изображения и една форма
import React, { useEffect, useState } from "react";
import Navbar from "./components/Navbar";
import "./index.css";

function AdminPageT() {
  const [products, setProducts] = useState([]);
  const [name, setName] = useState("");
  const [description, setDescription] = useState("");
  const [price, setPrice] = useState("");
  const [image, setImage] = useState("");
  const [fileName, setFileName] = useState("");
  const [editingId, setEditingId] = useState(null);

  useEffect(() => {
    fetchProducts();
  }, []);

  const fetchProducts = async () => {
    try {
      const res = await fetch("http://localhost:5000/api/admin");
      const data = await res.json();
      setProducts(Array.isArray(data) ? data : []);
    } catch (err) {
      console.error("❌ Грешка при зареждане:", err);
    }
  };

  const convertToBase64 = (file) => new Promise((resolve, reject) => {
    const reader = new FileReader();
    reader.readAsDataURL(file);
    reader.onload = () => resolve(reader.result);
    reader.onerror = (error) => reject(error);
  });

  const handleImageChange = async (e) => {
    const file = e.target.files[0];
    if (file) {
      const base64 = await convertToBase64(file);
      setImage(base64);
      setFileName(file.name);
    }
  };

  const handleAdd = async () => {
    if (!name || !description || !price || !image) {
      alert("Моля, попълнете всички полета.");
      return;
    }
    const method = editingId ? "PUT" : "POST";
    const url = editingId
      ? `http://localhost:5000/api/admin/${editingId}`
      : "http://localhost:5000/api/admin";

    try {
      const res = await fetch(url, {
        method,
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ name, description, image, price })
      });
      const data = await res.json();
      if (res.ok) {
        alert(data.message);
        setName(""); setDescription(""); setPrice(""); setImage("");
        setFileName(""); setEditingId(null);
        fetchProducts();
      } else alert("Грешка при заявката");
    } catch (err) {
      console.error("❌ Грешка при заявката:", err);
    }
  };

  const handleDelete = async (id) => {
    try {
      const res = await fetch(`http://localhost:5000/api/admin/${id}`, { method: "DELETE" });
      const data = await res.json();
      alert(data.message);
      fetchProducts();
    } catch (err) {
      console.error("❌ Грешка при изтриване:", err);
    }
  };

  return (
    <div className="admin-page">
      <Navbar />
      <div style={{ display: "flex", justifyContent: "center", gap: "2rem", flexWrap: "wrap" }}>
        <div className="admin-form">
          <h2>{editingId ? "Редакция на продукт" : "Добавяне на продукт"}</h2>
          <input type="text" placeholder="Име" value={name} onChange={(e) => setName(e.target.value)} />
          <input type="text" placeholder="Описание" value={description} onChange={(e) => setDescription(e.target.value)} />
          <input type="text" placeholder="Цена" value={price} onChange={(e) => setPrice(e.target.value)} />
          <label htmlFor="fileUpload" className="custom-file-upload">Изображение</label>
          <input id="fileUpload" type="file" accept="image/*" onChange={handleImageChange} style={{ display: "none" }} />
          {fileName && <p>Избран файл: {fileName}</p>}
          <button onClick={handleAdd}>{editingId ? "Запази" : "Добави"}</button>
        </div>
      </div>

      <div className="product-table">
        <table>
          <thead>
            <tr>
              <th>Снимка</th>
              <th>Име</th>
              <th>Описание</th>
              <th>Цена</th>
              <th>Действие</th>
            </tr>
          </thead>
          <tbody>
            {products.map((p) => (
              <tr key={p.ProductID}>
                <td><img src={p.ImageURL} alt={p.ProductName} style={{ width: 50 }} /></td>
                <td>{p.ProductName}</td>
                <td>{p.Description}</td>
                <td>{p.Price}</td>
                <td style={{ display: "flex", flexDirection: "column", gap: "0.5rem" }}>
                  <button className="edit-button" onClick={() => {
                    setEditingId(p.ProductID);
                    setName(p.ProductName);
                    setDescription(p.Description);
                    setPrice(p.Price);
                    setImage(p.ImageURL);
                    setFileName("съществуващо изображение");
                  }}>Редактирай</button>
                  <button className="delete-button" onClick={() => handleDelete(p.ProductID)}>Изтрий</button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
}

export default AdminPageT;
