  return (
    <section className="w-full min-h-screen bg-blue-800 text-white py-20 px-4 flex flex-col items-center text-center">
      {/* –ó–∞–≥–ª–∞–≤–∏–µ –∏ –±—É—Ç–æ–Ω */}
      <div className="mb-16">
        <h1 className="text-4xl md:text-6xl font-extrabold mb-4">
          The Best Antivirus Protection for 2025
        </h1>
        <p className="text-lg md:text-xl mb-8 max-w-2xl mx-auto">
          Comprehensive tests and reviews of the top antivirus solutions to keep your devices protected from the latest threats.
        </p>
        <a
          href="#top-picks"
          className="bg-white text-blue-700 px-6 py-3 rounded-full text-lg font-semibold hover:bg-gray-100 transition"
        >
          View Top Picks
        </a>
      </div>

      {/* –ò–Ω—Ñ–æ—Ä–º–∞—Ü–∏–æ–Ω–Ω–∏ –∫–∞—Ä—Ç–∏ */}
      <div className="max-w-7xl mx-auto grid grid-cols-1 md:grid-cols-3 gap-8">
        {[
          {
            icon: "üõ°Ô∏è",
            title: "Comprehensive Protection",
            desc: "We evaluate how effectively each solution protects against <br />malware, ransomware, and phishing attacks.",
          },
          {
            icon: "‚öôÔ∏è",
            title: "Performance Impact",
            desc: "We measure how each antivirus affects your system‚Äôs speed during scans and real-time protection.",
          },
          {
            icon: "üîí",
            title: "Additional Features",
            desc: "We evaluate extras like VPNs, password managers, and parental controls.",
          },
        ].map((card, i) => (
          <div
            key={i}
            className="bg-blue-900 p-6 rounded-xl shadow-md hover:shadow-lg transition duration-300"
          >
            <div className="text-4xl mb-4">{card.icon}</div>
            <h3 className="text-xl font-semibold mb-2">{card.title}</h3>
            <p className="text-sm">{card.desc}</p>
          </div>
        ))}
      </div>
    </section>
  );
}
