import React, { useState } from "react";

function Login() {
  const [fullName, setFullName] = useState("");
  const [password, setPassword] = useState("");

  const handleLogin = async (e) => {
    e.preventDefault();
    const res = await fetch("http://localhost:5000/api/login", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ fullName, password }),
    });
    const data = await res.json();
    alert(data.message || "Успешен вход!");
  };

  return (
    <div className="form-container">
      <h2>Вход</h2>
      <form onSubmit={handleLogin}>
        <input type="text" placeholder="Име" onChange={(e) => setFullName(e.target.value)} required />
        <input type="password" placeholder="Парола" onChange={(e) => setPassword(e.target.value)} required />
        <button type="submit">Вход</button>
      </form>
      <a href="/register">Нямате акаунт? Регистрирайте се</a>
    </div>
  );
}

export default Login;
