import React, { useEffect, useState } from "react";

function ReviewSection({ productId }) {
  const [reviews, setReviews] = useState([]);
  const [reviewText, setReviewText] = useState("");
  const [rating, setRating] = useState(5);
  const [user, setUser] = useState(null);

  useEffect(() => {
    const token = localStorage.getItem("token");
    const userData = token ? JSON.parse(atob(token.split(".")[1])) : null;
    setUser(userData);

    fetchReviews();
  }, [productId]);

  const fetchReviews = async () => {
    try {
      const res = await fetch(`http://localhost:5000/api/reviews/${productId}`);
      const data = await res.json();

      if (Array.isArray(data)) {
        setReviews(data);
      } else {
        console.warn("⚠️ Отговорът не е масив:", data);
        setReviews([]);
      }
    } catch (err) {
      console.error("❌ Грешка при зареждане на ревюта:", err);
      setReviews([]);
    }
  };

  const handleSubmit = async () => {
    if (!user || !reviewText.trim()) return;

    const token = localStorage.getItem("token");
    try {
      const response = await fetch("http://localhost:5000/api/reviews", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          Authorization: "Bearer " + token,
        },
        body: JSON.stringify({ productId, reviewText, rating }),
      });

      if (response.ok) {
        setReviewText("");
        setRating(5);
        fetchReviews();
      } else {
        console.warn("⚠️ Неуспешно добавяне на ревю");
      }
    } catch (err) {
      console.error("❌ Грешка при добавяне на ревю:", err);
    }
  };

  const handleDelete = async (reviewId) => {
    const token = localStorage.getItem("token");

    try {
      const response = await fetch(`http://localhost:5000/api/reviews/${reviewId}`, {
        method: "DELETE",
        headers: {
          Authorization: "Bearer " + token,
        },
      });

      if (response.ok) {
        fetchReviews();
      } else {
        console.warn("⚠️ Неуспешно изтриване на ревю");
      }
    } catch (err) {
      console.error("❌ Грешка при изтриване на ревю:", err);
    }
  };

  return (
    <div className="p-4 border-t mt-4">
      <h3 className="text-lg font-semibold mb-2">Ревюта</h3>

      {reviews.length > 0 ? (
        reviews.map((rev) => (
          <div key={rev.ReviewID} className="border p-2 rounded mb-2">
            <p className="font-semibold">Оценка: {rev.Rating} ★</p>
            <p>{rev.ReviewText}</p>
            {user?.isAdmin && (
              <button
                onClick={() => handleDelete(rev.ReviewID)}
                className="text-red-600 text-sm mt-1"
              >
                Изтрий
              </button>
            )}
          </div>
        ))
      ) : (
        <p className="text-sm text-gray-500">Все още няма ревюта за този продукт.</p>
      )}

      {user ? (
        <div className="mt-4">
          <textarea
            className="w-full border rounded p-2 mb-2"
            placeholder="Твоето мнение..."
            value={reviewText}
            onChange={(e) => setReviewText(e.target.value)}
          />
          <div className="flex items-center gap-2 mb-2">
            <label>Оценка:</label>
            <select
              value={rating}
              onChange={(e) => setRating(Number(e.target.value))}
              className="border p-1 rounded"
            >
              {[1, 2, 3, 4, 5].map((n) => (
                <option key={n} value={n}>
                  {n}
                </option>
              ))}
            </select>
          </div>
          <button
            onClick={handleSubmit}
            className="bg-blue-600 text-white px-4 py-2 rounded"
          >
            Добави ревю
          </button>
        </div>
      ) : (
        <p className="text-sm text-gray-600 mt-2">Трябва да си влязъл, за да оставиш ревю.</p>
      )}
    </div>
  );
}

export default ReviewSection;
