import React, { useState } from "react";

const faqs = [
  {
    question: "Do I really need antivirus software in 2025?",
    answer: "Yes, antivirus software is still essential to protect your devices from malware, phishing, and ransomware attacks that continue to evolve.",
  },
  {
    question: "What's the difference between free and paid antivirus software?",
    answer: "Free versions usually offer basic protection, while paid ones include advanced features like real-time scanning, firewalls, VPNs, and better support.",
  },
  {
    question: "Will antivirus software slow down my computer?",
    answer: "Modern antivirus software is optimized to run efficiently in the background. Some impact is possible, but it’s usually minimal.",
  },
  {
    question: "Can I use multiple antivirus programs for better protection?",
    answer: "It's not recommended. Running multiple antivirus programs can cause conflicts and reduce protection effectiveness.",
  },
  {
    question: "How often should I run an antivirus scan?",
    answer: "We recommend running a full system scan at least once a week and relying on real-time protection daily.",
  },
  {
    question: "Is Windows Defender good enough for basic protection?",
    answer: "Windows Defender offers decent protection for basic needs, but premium antivirus solutions provide stronger security and more features.",
  },
];

export default function FAQSection() {
  const [openIndex, setOpenIndex] = useState(null);

  const toggle = (index) => {
    setOpenIndex(index === openIndex ? null : index);
  };

  return (
    <section id="faq" className="bg-gray-100 py-20 px-6">
      <div className="max-w-4xl mx-auto">
        <h2 className="text-3xl font-extrabold text-center text-blue-800 mb-2">
          Frequently Asked Questions
        </h2>
        <p className="text-center text-gray-600 mb-10">
          Common questions about antivirus software and computer security.
        </p>

        <div className="bg-white rounded-xl shadow-md overflow-hidden">
          {faqs.map((faq, index) => (
            <div
              key={index}
              className="border-b last:border-b-0 cursor-pointer transition"
              onClick={() => toggle(index)}
            >
              <div className="px-6 py-4 text-sm font-medium hover:bg-gray-50 flex justify-between items-center">
                {faq.question}
                <span className="text-gray-400">{openIndex === index ? "▲" : "▼"}</span>
              </div>
              {openIndex === index && (
                <div className="px-6 pb-4 text-sm text-gray-700 transition duration-300 ease-in-out">
                  {faq.answer}
                </div>
              )}
            </div>
          ))}
        </div>
      </div>
    </section>
  );
}
