import React from "react";

const expertData = [
  {
    name: "Bitdefender Total Security",
    rating: 4.8,
    pros: [
      "Excellent malware detection rates",
      "Minimal system impact",
      "Comprehensive feature set",
      "Intuitive user interface",
      "Effective ransomware protection",
    ],
    cons: [
      "VPN has data limitations on basic plans",
      "Advanced features can be overwhelming",
      "Full system scans can be time-consuming",
    ],
    results: {
      Malware: 4.9,
      Impact: 2.9,
      Interface: 4.7,
      Value: 4.6,
    },
  },
  // –ú–æ–∂–µ—à –¥–∞ –¥–æ–±–∞–≤–∏—à –æ—â–µ –∞–Ω—Ç–∏–≤–∏—Ä—É—Å–∏ —Ç—É–∫
];

function ExpertReviews() {
  return (
    <div className="max-w-5xl mx-auto mt-12 bg-white rounded-lg p-6 shadow-md">
      <h2 className="text-2xl font-bold mb-4 text-center text-blue-600">Expert Reviews</h2>
      <p className="text-center text-gray-500 mb-6">
        Our in-depth analysis of each antivirus solution based on extensive testing.
      </p>
      {expertData.map((item, idx) => (
        <div key={idx} className="bg-gray-50 p-4 mb-6 rounded-md shadow-sm">
          <h3 className="font-bold text-lg mb-1">{item.name}</h3>
          <p className="text-sm text-yellow-500 mb-2">‚≠ê {item.rating} / 5.0</p>

          <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
            <div>
              <h4 className="font-semibold mb-1 text-green-700">Pros:</h4>
              <ul className="list-disc list-inside text-sm text-gray-700">
                {item.pros.map((p, i) => (
                  <li key={i}>+ {p}</li>
                ))}
              </ul>
              <h4 className="font-semibold mt-4 mb-1 text-red-700">Cons:</h4>
              <ul className="list-disc list-inside text-sm text-gray-700">
                {item.cons.map((c, i) => (
                  <li key={i}>- {c}</li>
                ))}
              </ul>
            </div>

            <div>
              <h4 className="font-semibold mb-2">Test Results</h4>
              <ul className="text-sm text-gray-700 space-y-1">
                <li>üß™ Malware Protection: {item.results.Malware}/5</li>
                <li>‚öôÔ∏è Performance Impact: {item.results.Impact}/5</li>
                <li>üñ•Ô∏è User Interface: {item.results.Interface}/5</li>
                <li>üí∞ Value for Money: {item.results.Value}/5</li>
              </ul>
            </div>
          </div>
        </div>
      ))}
    </div>
  );
}

export default ExpertReviews;
