import React, { useEffect, useState } from "react";

function AdminProducts() {
  const [products, setProducts] = useState([]);
  const [editingProduct, setEditingProduct] = useState(null);
  const [form, setForm] = useState({
    name: "",
    description: "",
    price: "",
    image: "",
  });
  const [fileName, setFileName] = useState("");

  useEffect(() => {
    fetchProducts();
  }, []);

  const fetchProducts = async () => {
    const res = await fetch("http://localhost:5000/api/admin");
    const data = await res.json();
    if (Array.isArray(data)) setProducts(data);
  };

  const convertToBase64 = (file) =>
    new Promise((resolve, reject) => {
      const reader = new FileReader();
      reader.readAsDataURL(file);
      reader.onload = () => resolve(reader.result);
      reader.onerror = (error) => reject(error);
    });

  const handleImageChange = async (e) => {
    const file = e.target.files[0];
    if (file) {
      const base64 = await convertToBase64(file);
      setForm({ ...form, image: base64 });
      setFileName(file.name);
    }
  };

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const method = editingProduct ? "PUT" : "POST";
    const url = editingProduct
      ? `http://localhost:5000/api/admin/${editingProduct.ProductID}`
      : "http://localhost:5000/api/admin";

    const res = await fetch(url, {
      method,
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(form),
    });

    const data = await res.json();
    alert(data.message || "Success!");
    setForm({ name: "", description: "", price: "", image: "" });
    setFileName("");
    setEditingProduct(null);
    fetchProducts();
  };

  const handleEdit = (product) => {
    setEditingProduct(product);
    setForm({
      name: product.ProductName,
      description: product.Description,
      price: product.Price,
      image: product.ImageURL,
    });
  };

  const handleDelete = async (id) => {
    const confirmDelete = window.confirm("Are you sure?");
    if (!confirmDelete) return;

    await fetch(`http://localhost:5000/api/admin/${id}`, {
      method: "DELETE",
    });

    fetchProducts();
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-gray-900 to-gray-700 text-white p-8">
      <h1 className="text-3xl font-bold mb-6 text-center">🧾 Manage Products</h1>

      <form
        onSubmit={handleSubmit}
        className="bg-white/10 backdrop-blur p-6 rounded-xl shadow-md max-w-xl mx-auto mb-10"
      >
        <h2 className="text-xl font-semibold mb-4">{editingProduct ? "Edit Product" : "Add Product"}</h2>

        <input
          type="text"
          name="name"
          placeholder="Product Name"
          value={form.name}
          onChange={handleChange}
          required
          className="w-full mb-3 p-2 rounded bg-white/20 text-white placeholder-white/70"
        />
        <input
          type="text"
          name="description"
          placeholder="Description"
          value={form.description}
          onChange={handleChange}
          required
          className="w-full mb-3 p-2 rounded bg-white/20 text-white placeholder-white/70"
        />
        <input
          type="text"
          name="price"
          placeholder="Price"
          value={form.price}
          onChange={handleChange}
          required
          className="w-full mb-3 p-2 rounded bg-white/20 text-white placeholder-white/70"
        />
        <label className="block mb-2 text-sm font-medium text-white">
          Upload Image
        </label>
        <input
          type="file"
          accept="image/*"
          onChange={handleImageChange}
          className="mb-4"
        />
        {fileName && <p className="text-sm text-green-400">📷 {fileName}</p>}

        <button
          type="submit"
          className="bg-blue-600 hover:bg-blue-700 px-4 py-2 rounded w-full font-bold transition"
        >
          {editingProduct ? "Update Product" : "Add Product"}
        </button>
      </form>

      <div className="grid gap-6 max-w-6xl mx-auto">
        {products.map((product) => (
          <div
            key={product.ProductID}
            className="bg-white/10 p-6 rounded-xl shadow-md flex justify-between items-center"
          >
            <div className="flex items-center gap-4">
              <img
                src={
                  product.ImageURL?.startsWith("http")
                    ? product.ImageURL
                    : `http://localhost:5000${product.ImageURL}`
                }
                alt={product.ProductName}
                className="w-16 h-16 object-contain rounded"
              />
              <div>
                <h3 className="text-xl font-semibold">{product.ProductName}</h3>
                <p className="text-sm text-gray-300">{product.Description}</p>
                <p className="text-sm text-gray-400">💵 ${product.Price}</p>
              </div>
            </div>
            <div className="flex gap-2">
              <button
                onClick={() => handleEdit(product)}
                className="bg-yellow-500 hover:bg-yellow-600 px-4 py-2 rounded text-black font-bold"
              >
                ✏️ Edit
              </button>
              <button
                onClick={() => handleDelete(product.ProductID)}
                className="bg-red-600 hover:bg-red-700 px-4 py-2 rounded text-white font-bold"
              >
                🗑 Delete
              </button>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}

export default AdminProducts;
