// ‚úÖ Admin Panel —Å –Ω–æ–≤–∞ —Ç–∞–±–ª–∏—Ü–∞ –∑–∞ Expert Reviews
import React, { useEffect, useState } from "react";
import Navbar from "./components/Navbar";
import "./index.css";

function AdminPageT() {
  const [products, setProducts] = useState([]);
  const [reviews, setReviews] = useState([]);
  const [selectedProductId, setSelectedProductId] = useState(null);

  const [reviewData, setReviewData] = useState({
    ourReview: "",
    pros: "",
    cons: "",
    bottomLine: "",
    malware: "",
    performance: "",
    ui: "",
    value: "",
    features: ""
  });

  useEffect(() => {
    fetchProducts();
    fetchReviews();
  }, []);

  const fetchProducts = async () => {
    try {
      const res = await fetch("http://localhost:5000/api/admin");
      const data = await res.json();
      setProducts(Array.isArray(data) ? data : []);
    } catch (err) {
      console.error("‚ùå –ì—Ä–µ—à–∫–∞ –ø—Ä–∏ –∑–∞—Ä–µ–∂–¥–∞–Ω–µ –Ω–∞ –ø—Ä–æ–¥—É–∫—Ç–∏:", err);
    }
  };

  const fetchReviews = async () => {
    try {
      const res = await fetch("http://localhost:5000/api/expert");
      const data = await res.json();
      setReviews(Array.isArray(data) ? data : []);
    } catch (err) {
      console.error("‚ùå –ì—Ä–µ—à–∫–∞ –ø—Ä–∏ –∑–∞—Ä–µ–∂–¥–∞–Ω–µ –Ω–∞ —Ä–µ–≤—é—Ç–∞:", err);
    }
  };

  const handleSaveReview = async () => {
    if (!selectedProductId) return alert("–ú–æ–ª—è, –∏–∑–±–µ—Ä–∏ –ø—Ä–æ–¥—É–∫—Ç.");

    try {
      const res = await fetch(`http://localhost:5000/api/expert/${selectedProductId}`, {
        method: "PUT",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(reviewData)
      });

      const data = await res.json();
      alert(data.message || "–£—Å–ø–µ—à–Ω–æ –∑–∞–ø–∏—Å–∞–Ω–æ!");
      fetchReviews();
    } catch (err) {
      console.error("‚ùå –ì—Ä–µ—à–∫–∞ –ø—Ä–∏ –∑–∞–ø–∏—Å:", err);
      alert("–ì—Ä–µ—à–∫–∞ –ø—Ä–∏ –∑–∞–ø–∏—Å –Ω–∞ —Ä–µ–≤—é—Ç–æ");
    }
  };

  const handleEditReview = (r) => {
    setSelectedProductId(r.ProductID);
    setReviewData({
      ourReview: r.OurReview || "",
      pros: r.Pros || "",
      cons: r.Cons || "",
      bottomLine: r.BottomLine || "",
      malware: r.Malware || "",
      performance: r.Performance || "",
      ui: r.UI || "",
      value: r.Value || "",
      features: r.ProtectionFeatures || ""
    });
  };

  const handleDeleteReview = async (productId) => {
    try {
      const res = await fetch(`http://localhost:5000/api/expert/${productId}`, {
        method: "DELETE"
      });

      const data = await res.json();
      alert(data.message || "–ò–∑—Ç—Ä–∏—Ç–æ —É—Å–ø–µ—à–Ω–æ");
      fetchReviews();
    } catch (err) {
      console.error("‚ùå –ì—Ä–µ—à–∫–∞ –ø—Ä–∏ –∏–∑—Ç—Ä–∏–≤–∞–Ω–µ:", err);
    }
  };

  return (
    <div className="admin-page">
      <Navbar />
      <div className="p-6 max-w-7xl mx-auto">
        <h2 className="text-2xl font-bold mb-4">Expert Reviews</h2>

        <div className="grid grid-cols-1 md:grid-cols-2 gap-8">
          {/* –¢–∞–±–ª–∏—Ü–∞ */}
          <div>
            <table className="min-w-full bg-white rounded shadow">
              <thead className="bg-gray-200">
                <tr>
                  <th className="p-2">–ü—Ä–æ–¥—É–∫—Ç</th>
                  <th className="p-2">–î–µ–π—Å—Ç–≤–∏—è</th>
                </tr>
              </thead>
              <tbody>
                {reviews.map((r) => (
                  <tr key={r.ProductID} className="border-b">
                    <td className="p-2">{r.ProductName}</td>
                    <td className="p-2 flex gap-2">
                      <button className="edit-button" onClick={() => handleEditReview(r)}>–†–µ–¥–∞–∫—Ç–∏—Ä–∞–π</button>
                      <button className="delete-button" onClick={() => handleDeleteReview(r.ProductID)}>–ò–∑—Ç—Ä–∏–π</button>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>

          {/* –§–æ—Ä–º–∞ */}
          <div className="bg-white p-4 rounded shadow space-y-3">
            <h3 className="text-lg font-semibold">{selectedProductId ? "–†–µ–¥–∞–∫—Ü–∏—è –Ω–∞ —Ä–µ–≤—é" : "–ò–∑–±–µ—Ä–∏ –ø—Ä–æ–¥—É–∫—Ç"}</h3>
            <select
              className="input w-full"
              value={selectedProductId || ""}
              onChange={(e) => setSelectedProductId(e.target.value)}
            >
              <option value="">-- –ò–∑–±–µ—Ä–∏ –ø—Ä–æ–¥—É–∫—Ç --</option>
              {products.map((p) => (
                <option key={p.ProductID} value={p.ProductID}>{p.ProductName}</option>
              ))}
            </select>

            <textarea className="textarea" placeholder="Our Review" value={reviewData.ourReview} onChange={(e) => setReviewData({ ...reviewData, ourReview: e.target.value })} />
            <textarea className="textarea" placeholder="Pros (–ø–æ —Ä–µ–¥–æ–≤–µ)" value={reviewData.pros} onChange={(e) => setReviewData({ ...reviewData, pros: e.target.value })} />
            <textarea className="textarea" placeholder="Cons (–ø–æ —Ä–µ–¥–æ–≤–µ)" value={reviewData.cons} onChange={(e) => setReviewData({ ...reviewData, cons: e.target.value })} />
            <textarea className="textarea" placeholder="Bottom Line" value={reviewData.bottomLine} onChange={(e) => setReviewData({ ...reviewData, bottomLine: e.target.value })} />

            <input className="input" placeholder="Malware Protection" value={reviewData.malware} onChange={(e) => setReviewData({ ...reviewData, malware: e.target.value })} />
            <input className="input" placeholder="Performance Impact" value={reviewData.performance} onChange={(e) => setReviewData({ ...reviewData, performance: e.target.value })} />
            <input className="input" placeholder="User Interface" value={reviewData.ui} onChange={(e) => setReviewData({ ...reviewData, ui: e.target.value })} />
            <input className="input" placeholder="Value for Money" value={reviewData.value} onChange={(e) => setReviewData({ ...reviewData, value: e.target.value })} />

            <textarea className="textarea" placeholder="Protection Features (–ø–æ —Ä–µ–¥–æ–≤–µ)" value={reviewData.features} onChange={(e) => setReviewData({ ...reviewData, features: e.target.value })} />

            <button className="btn-blue w-full" onClick={handleSaveReview}>üíæ –ó–∞–ø–∏—à–∏ —Ä–µ–≤—é</button>
          </div>
        </div>
      </div>
    </div>
  );
}

export default AdminPageT;
