import React, { useState, useEffect } from "react";

function ExpertReviews() {
  const [products, setProducts] = useState([]);
  const [expandedId, setExpandedId] = useState(null);

  useEffect(() => {
    fetch("http://localhost:5000/api/admin") // Вземаме всички продукти с нужните полета
      .then((res) => res.json())
      .then((data) => setProducts(data))
      .catch((err) => console.error("Грешка при зареждане на продукти:", err));
  }, []);

  const toggleExpand = (id) => {
    setExpandedId(expandedId === id ? null : id);
  };

  return (
    <section className="max-w-4xl mx-auto p-4 bg-white rounded-md shadow-md my-8">
      <h2 className="text-xl font-bold mb-1 text-center text-blue-700">Expert Reviews</h2>
      <p className="text-center text-gray-600 mb-6 text-sm">
        Our in-depth analysis of each antivirus solution based on extensive testing.
      </p>

      {products.map((product) => (
        <div key={product.ProductID} className="border rounded mb-4 overflow-hidden">
          <button
            onClick={() => toggleExpand(product.ProductID)}
            className="flex items-center justify-between w-full px-4 py-3 text-left bg-gray-100 hover:bg-gray-200 cursor-pointer"
          >
            <div className="flex items-center gap-3">
              <img src={product.ImageURL} alt={product.ProductName} className="w-10 h-10 object-contain rounded" />
              <div>
                <h3 className="font-semibold">{product.ProductName}</h3>
                <p className="text-yellow-500 text-sm">
                  ⭐ {product.AverageRating ? product.AverageRating.toFixed(1) : "N/A"} / 5.0
                </p>
              </div>
            </div>
            <span className="text-xl">{expandedId === product.ProductID ? "▲" : "▼"}</span>
          </button>

          {expandedId === product.ProductID && (
            <div className="p-4 bg-white text-gray-700 text-sm leading-relaxed flex flex-col md:flex-row gap-6">
              {/* Лява част - Review Text */}
              <div className="md:flex-1">
                <h4 className="font-semibold mb-2">Our Review</h4>
                <p>
                  {/* Тук можеш да сложиш реалния текст от база или API */}
                  {product.ReviewText || "No review text available."}
                </p>

                <div className="flex gap-8 mt-4">
                  <div>
                    <h5 className="text-green-600 font-semibold mb-1">Pros:</h5>
                    <ul className="list-disc ml-5 text-green-600">
                      {(product.Pros || []).map((item, i) => (
                        <li key={i}>{item}</li>
                      ))}
                    </ul>
                  </div>
                  <div>
                    <h5 className="text-red-600 font-semibold mb-1">Cons:</h5>
                    <ul className="list-disc ml-5 text-red-600">
                      {(product.Cons || []).map((item, i) => (
                        <li key={i}>{item}</li>
                      ))}
                    </ul>
                  </div>
                </div>

                <h5 className="mt-4 font-semibold">Bottom Line</h5>
                <p>{product.BottomLine || "Summary not available."}</p>
              </div>

              {/* Дясна част - Test Results */}
              <div className="md:w-64 bg-gray-100 p-4 rounded">
                <h5 className="font-semibold mb-2">Test Results</h5>
                <ul className="space-y-2 text-sm">
                  {(product.TestResults || []).map((test, i) => (
                    <li key={i} className="flex justify-between items-center">
                      <span>{test.label}</span>
                      <span className="font-semibold">{test.score}</span>
                    </li>
                  ))}
                </ul>

                <h5 className="font-semibold mt-4 mb-2">Protection Features</h5>
                <ul className="space-y-1 text-xs">
                  {(product.ProtectionFeatures || []).map((feat, i) => (
                    <li key={i}>
                      <input type="checkbox" checked readOnly /> <span>{feat}</span>
                    </li>
                  ))}
                </ul>

                <button
                  onClick={() => window.open(product.OfficialWebsite, "_blank")}
                  className="mt-4 bg-blue-600 text-white w-full py-2 rounded hover:bg-blue-700"
                >
                  Visit Official Website
                </button>
              </div>
            </div>
          )}
        </div>
      ))}
    </section>
  );
}

export default ExpertReviews;
