import React, { useState } from "react";
import Navbar from "./components/Navbar"; // включваме Navbar

function AdminPage() {
  const [showPanel, setShowPanel] = useState(false);
  const [name, setName] = useState("");
  const [description, setDescription] = useState("");
  const [price, setPrice] = useState("");

  const handleAdd = async () => {
    try {
      const response = await fetch("http://localhost:5000/api/products", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ name, description, price }),
      });

      const data = await response.json();
      alert(data.message || "Продуктът е добавен!");
      setName("");
      setDescription("");
      setPrice("");
    } catch (error) {
      console.error("Грешка при добавяне:", error);
    }
  };

  return (
    <div className="min-h-screen bg-gray-100">
      <Navbar />

      <div className="pt-32 flex flex-col items-center">
        {!showPanel ? (
          <button
            onClick={() => setShowPanel(true)}
            className="bg-green-600 text-white px-6 py-3 rounded-lg shadow hover:bg-green-700 transition"
          >
            Админ Панел
          </button>
        ) : (
          <div className="bg-white shadow-lg p-8 rounded-lg w-full max-w-md animate-fade-in">
            <h2 className="text-xl font-bold text-center mb-6">Добавяне на продукт</h2>

            <input
              type="text"
              placeholder="Име"
              value={name}
              onChange={(e) => setName(e.target.value)}
              className="w-full mb-4 px-4 py-2 border rounded focus:outline-none focus:ring-2 focus:ring-purple-500"
            />

            <input
              type="text"
              placeholder="Описание"
              value={description}
              onChange={(e) => setDescription(e.target.value)}
              className="w-full mb-4 px-4 py-2 border rounded focus:outline-none focus:ring-2 focus:ring-purple-500"
            />

            <input
              type="number"
              placeholder="Цена"
              value={price}
              onChange={(e) => setPrice(e.target.value)}
              className="w-full mb-4 px-4 py-2 border rounded focus:outline-none focus:ring-2 focus:ring-purple-500"
            />

            <button
              onClick={handleAdd}
              className="w-full bg-blue-600 text-white font-semibold py-2 px-4 rounded hover:bg-blue-700 transition"
            >
              Добави
            </button>
          </div>
        )}
      </div>
    </div>
  );
}

export default AdminPage;
