import React, { useEffect, useState } from "react";
import "./AntivirusList.css";

function AntivirusList() {
  const [products, setProducts] = useState([]);

  useEffect(() => {
    fetch("http://localhost:5000/api/admin/products")
      .then((res) => res.json())
      .then((data) => {
        if (Array.isArray(data)) {
          setProducts(data);
        } else {
          console.warn("⚠️ Data is not array", data);
        }
      })
      .catch((err) => console.error("❌ Error loading antivirus products:", err));
  }, []);

  return (
    <div className="antivirus-list">
      <h2 className="title">Нашите Антивирусни Предложения</h2>
      <div className="grid">
        {products.map((product) => (
          <div className="card" key={product.ProductID}>
            <img src={product.ImageURL} alt={product.ProductName} className="card-img" />
            <h3>{product.ProductName}</h3>
            <p>{product.Description}</p>
            <p className="price">{parseFloat(product.Price).toFixed(2)} лв.</p>
            <button className="read-btn">Прочети ревю</button>
          </div>
        ))}
      </div>
    </div>
  );
}

export default AntivirusList;
