import React, { useEffect, useState } from "react";
import { useParams, useNavigate } from "react-router-dom";
import ReviewSection from "./components/ReviewSection";

function ProductDetails() {
  const { name } = useParams();
  const [product, setProduct] = useState(null);
  const [features, setFeatures] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    fetch(`http://localhost:5000/api/admin/name/${name}`)
      .then((res) => {
        if (!res.ok) throw new Error("–ü—Ä–æ–¥—É–∫—Ç—ä—Ç –Ω–µ –µ –Ω–∞–º–µ—Ä–µ–Ω");
        return res.json();
      })
      .then((data) => setProduct(data))
      .catch((err) => {
        console.error("‚ùå –ì—Ä–µ—à–∫–∞ –ø—Ä–∏ –∑–∞—Ä–µ–∂–¥–∞–Ω–µ:", err);
        navigate("/");
      });

    fetch(`http://localhost:5000/api/products/${name}/features`)
      .then((res) => res.json())
      .then((data) => setFeatures(data))
      .catch((err) => console.error("‚ùå –ì—Ä–µ—à–∫–∞ –ø—Ä–∏ –∑–∞—Ä–µ–∂–¥–∞–Ω–µ –Ω–∞ features:", err));
  }, [name, navigate]);

  if (!product) {
    return <div className="p-8 text-center">–ó–∞—Ä–µ–∂–¥–∞–Ω–µ...</div>;
  }

  return (
    <div className="bg-gray-100 min-h-screen py-8">
      <div className="mb-6 px-6">
        <button
          onClick={() => navigate("/")}
          className="bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700 transition"
        >
          üè† –ù–∞—á–∞–ª–æ
        </button>
      </div>

      <div className="max-w-4xl mx-auto bg-white rounded-xl shadow-lg p-6">
        <div className="flex flex-col md:flex-row gap-6 items-center">
          <img
            src={product.ImageURL || "/placeholder.png"}
            alt={product.ProductName}
            className="w-28 h-28 object-contain"
          />
          <div className="flex-1">
            <h1 className="text-3xl font-bold text-gray-900 mb-2">
              {product.ProductName}
            </h1>
            <p className="text-gray-700 mb-4 text-sm md:text-base">
              {product.Description}
            </p>
            <p className="text-green-600 font-semibold text-lg">–¶–µ–Ω–∞: {product.Price} –ª–≤</p>
            {typeof product.AverageRating === "number" && !isNaN(product.AverageRating) && (
              <p className="text-yellow-500 mt-1 font-medium">
                ‚≠ê {product.AverageRating.toFixed(1)} / 5.0
              </p>
            )}
          </div>
        </div>

        {features.length > 0 && (
          <div className="mt-6">
            <h3 className="text-lg font-semibold text-gray-800 mb-2">–û—Å–Ω–æ–≤–Ω–∏ —Ö–∞—Ä–∞–∫—Ç–µ—Ä–∏—Å—Ç–∏–∫–∏:</h3>
            <ul className="list-disc list-inside text-gray-700 space-y-1 text-sm">
              {features.map((f, i) => (
                <li key={i}>{f.Feature || f}</li>
              ))}
            </ul>
          </div>
        )}

        <div className="border-t border-gray-200 mt-8 pt-6">
          <ReviewSection productId={product.ProductID} />
        </div>
      </div>
    </div>
  );
}

export default ProductDetails;
