import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import "../index.css";

function AntivirusListT() {
  const [products, setProducts] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    fetch("http://localhost:5000/api/admin/products") // ✅ ВАЖНО: правилният URL
      .then((res) => res.json())
      .then((data) => {
        if (Array.isArray(data)) {
          setProducts(data);
        } else {
          setProducts([]);
        }
      })
      .catch((err) => {
        console.error("❌ Грешка при зареждане на продукти:", err);
        setProducts([]);
      });
  }, []);

  return (
    <div className="antivirus-list">
      <h2 className="title">Нашите Антивирусни Предложения</h2>
      <div className="product-row">
        {products.length > 0 ? (
          products.map((product) => (
            <div className="product-card" key={product.ProductID}>
              <img
                src={product.ImageURL}
                alt={product.ProductName}
                className="product-img"
              />
              <h3 className="product-name">{product.ProductName}</h3>
              <p className="description">{product.Description}</p>
              <p className="price">{product.Price}</p>
              <button
                className="btn-view"
                onClick={() => navigate(`/product/${product.ProductName}`)}
              >
                Ревю
              </button>
            </div>
          ))
        ) : (
          <p style={{ textAlign: "center", marginTop: "2rem" }}>
            Няма налични продукти.
          </p>
        )}
      </div>
    </div>
  );
}

export default AntivirusListT;
