import React from "react";

export default function Hero() {
  return (
    
    <section className="w-full h-screen bg-gradient-to-r from-indigo-500 to-purple-600 text-white flex flex-col justify-center items-center text-center px-4 pt-20">
      <h1 className="text-4xl md:text-6xl font-extrabold mb-4">
        Compare the Best Antivirus Software of 2025
      </h1>
      <p className="text-lg md:text-xl mb-8 max-w-2xl">
        Independent expert reviews to help you choose the right protection.
      </p>
      <a
        href="#top-picks"
        className="bg-white text-indigo-600 px-6 py-3 rounded-full text-lg font-semibold hover:bg-gray-200 transition"
      >
        View Top Picks
      </a>
    </section>
  );
}
