import React, { useState, useEffect } from "react";

function ExpertReviews() {
  const [products, setProducts] = useState([]);
  const [expandedId, setExpandedId] = useState(null);

  useEffect(() => {
    fetch("http://localhost:5000/api/admin")
      .then((res) => res.json())
      .then((data) => setProducts(data))
      .catch((err) => console.error("❌ Expert Review Fetch Error:", err));
  }, []);

  const toggleExpand = (id) => {
    setExpandedId(expandedId === id ? null : id);
  };

  const parseLines = (text) =>
    typeof text === "string" ? text.split("\n").filter((line) => line.trim() !== "") : [];

  return (
    <section className="max-w-5xl mx-auto p-6 bg-white rounded-lg shadow-lg my-10">
      <h2 className="text-2xl font-bold mb-2 text-center text-blue-700">Expert Reviews</h2>
      <p className="text-center text-gray-600 mb-6 text-sm">
        Our in-depth analysis of each antivirus solution based on extensive testing.
      </p>

      {products.map((product) => (
        <div key={product.ProductID} className="border border-gray-300 rounded mb-6 overflow-hidden">
          <button
            onClick={() => toggleExpand(product.ProductID)}
            className="flex items-center justify-between w-full px-5 py-3 text-left bg-gray-100 hover:bg-gray-200 cursor-pointer"
          >
            <div className="flex items-center gap-4">
              <img
                src={product.ImageURL}
                alt={product.ProductName}
                className="w-12 h-12 object-contain rounded"
              />
              <div>
                <h3 className="font-semibold">{product.ProductName}</h3>
                <p className="text-yellow-500 text-sm">
                  ⭐ {product.AverageRating ? product.AverageRating.toFixed(1) : "N/A"} / 5.0
                </p>
              </div>
            </div>
            <span className="text-2xl">{expandedId === product.ProductID ? "▲" : "▼"}</span>
          </button>

          {expandedId === product.ProductID && (
            <div className="flex flex-col md:flex-row bg-white p-6 text-gray-700 text-sm gap-8">
              <div className="md:flex-1">
                <h4 className="font-semibold mb-3">Our Review</h4>
                <p className="mb-4 whitespace-pre-line">{product.ReviewText || "No review available."}</p>
                <div className="flex gap-12">
                  <div>
                    <h5 className="text-green-600 font-semibold mb-2">Pros:</h5>
                    <ul className="list-disc ml-5 text-green-600 space-y-1">
                      {parseLines(product.Pros).map((pro, i) => (
                        <li key={i}>{pro}</li>
                      ))}
                    </ul>
                  </div>
                  <div>
                    <h5 className="text-red-600 font-semibold mb-2">Cons:</h5>
                    <ul className="list-disc ml-5 text-red-600 space-y-1">
                      {parseLines(product.Cons).map((con, i) => (
                        <li key={i}>{con}</li>
                      ))}
                    </ul>
                  </div>
                </div>
                <h5 className="mt-6 font-semibold">Bottom Line</h5>
                <p>{product.BottomLine || "Summary not available."}</p>
              </div>

              <div className="md:w-64 bg-gray-100 p-5 rounded">
                <h5 className="font-semibold mb-4">Test Results</h5>
                <ul className="space-y-3 text-sm">
                  {Array.isArray(product.TestResults) &&
                    product.TestResults.map((test, i) => (
                      <li key={i} className="flex justify-between items-center">
                        <span>{test.label}</span>
                        <span className="font-semibold">{test.score}</span>
                      </li>
                    ))}
                </ul>

                <h5 className="font-semibold mt-6 mb-3">Protection Features</h5>
                <ul className="space-y-1 text-xs">
                  {parseLines(product.ProtectionFeatures).map((feature, i) => (
                    <li key={i} className="flex items-center gap-2">
                      <input type="checkbox" checked readOnly />
                      <span>{feature}</span>
                    </li>
                  ))}
                </ul>

                <button
                  onClick={() => window.open(product.OfficialWebsite, "_blank")}
                  className="mt-6 bg-blue-600 text-white w-full py-2 rounded hover:bg-blue-700"
                >
                  Visit Official Website
                </button>
              </div>
            </div>
          )}
        </div>
      ))}
    </section>
  );
}

export default ExpertReviews;
