import React, { useState, useContext } from "react";
import { useNavigate } from "react-router-dom";
import { AuthContext } from "./context/AuthContext";
import { FaUser, FaLock } from "react-icons/fa";

function Login() {
  const [fullName, setFullName] = useState("");
  const [password, setPassword] = useState("");
  const navigate = useNavigate();
  const { login } = useContext(AuthContext);

  const handleLogin = async (e) => {
    e.preventDefault();

    if (!fullName || !password) {
      alert("Моля, въведете име и парола.");
      return;
    }

    try {
      const res = await fetch("http://localhost:5000/api/login", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ fullName, password }),
      });

      const data = await res.json();

      if (res.ok) {
        login(data.token, data.user.isAdmin);
        alert(data.message || "Успешен вход!");
        navigate("/");
      } else {
        alert(data.message || "Грешка при входа.");
      }
    } catch (err) {
      alert("Възникна грешка при връзката със сървъра.");
      console.error(err);
    }
  };

  return (
    <div className="flex items-center justify-center min-h-screen bg-gradient-to-br from-indigo-500 via-purple-500 to-pink-500">
      <form
        onSubmit={handleLogin}
        className="bg-white shadow-2xl rounded-2xl p-10 w-full max-w-md animate-fade-in"
      >
        <h2 className="text-3xl font-bold text-center mb-8 text-gray-800">Добре дошъл!</h2>

        <div className="mb-6 relative">
          <FaUser className="absolute left-3 top-3 text-gray-400" />
          <input
            type="text"
            placeholder="Вашето име"
            value={fullName}
            onChange={(e) => setFullName(e.target.value)}
            required
            className="w-full pl-10 pr-4 py-2 border border-gray-300 rounded focus:outline-none focus:ring-2 focus:ring-purple-400 transition-all"
          />
        </div>

        <div className="mb-6 relative">
          <FaLock className="absolute left-3 top-3 text-gray-400" />
          <input
            type="password"
            placeholder="Парола"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
            className="w-full pl-10 pr-4 py-2 border border-gray-300 rounded focus:outline-none focus:ring-2 focus:ring-purple-400 transition-all"
          />
        </div>

        <button
          type="submit"
          className="w-full bg-purple-600 hover:bg-purple-700 text-white font-bold py-2 px-4 rounded transition-all"
        >
          Вход
        </button>

        <p className="mt-6 text-center text-sm text-gray-700">
          Нямате акаунт?{" "}
          <a
            href="/register"
            className="text-pink-600 hover:underline font-semibold"
          >
            Регистрирайте се
          </a>
        </p>
      </form>
    </div>
  );
}

export default Login;
