import React, { useEffect, useState } from "react";

function ReviewSection({ productId }) {
  const [reviews, setReviews] = useState([]);
  const [reviewText, setReviewText] = useState("");
  const [rating, setRating] = useState(5);
  const [user, setUser] = useState(null);
  const [isAdmin, setIsAdmin] = useState(false);

  useEffect(() => {
    const token = localStorage.getItem("token");
    const userData = token ? JSON.parse(atob(token.split(".")[1])) : null;
    setUser(userData);
    setIsAdmin(userData?.isAdmin === true);

    fetch(`http://localhost:5000/api/reviews/${productId}`)
      .then((res) => res.json())
      .then((data) => setReviews(data));
  }, [productId]);

  const handleSubmit = async () => {
    if (!user) return;

    const token = localStorage.getItem("token");
    try {
      const response = await fetch("http://localhost:5000/api/reviews", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          Authorization: "Bearer " + token,
        },
        body: JSON.stringify({ productId, reviewText, rating })
      });

      if (response.ok) {
        const updated = await response.json();
        setReviews((prev) => [...prev, updated]);
        setReviewText("");
        setRating(5);
      }
    } catch (err) {
      console.error("‚ùå –ì—Ä–µ—à–∫–∞ –ø—Ä–∏ –∏–∑–ø—Ä–∞—â–∞–Ω–µ –Ω–∞ —Ä–µ–≤—é:", err);
    }
  };

  const handleDelete = async (id) => {
    const token = localStorage.getItem("token");
    if (!token || !isAdmin) return;

    try {
      await fetch(`http://localhost:5000/api/reviews/${id}`, {
        method: "DELETE",
        headers: {
          Authorization: "Bearer " + token
        }
      });
      setReviews((prev) => prev.filter((r) => r.ReviewID !== id));
    } catch (err) {
      console.error("‚ùå –ì—Ä–µ—à–∫–∞ –ø—Ä–∏ –∏–∑—Ç—Ä–∏–≤–∞–Ω–µ:", err);
    }
  };

  const renderStars = (value) => (
    <span className="text-yellow-500">
      {Array.from({ length: 5 }, (_, i) => (
        <span key={i}>{i < value ? "‚òÖ" : "‚òÜ"}</span>
      ))}
    </span>
  );

  return (
    <div className="bg-white p-6 rounded-xl shadow">
      <h3 className="text-lg font-semibold text-center text-blue-700 mb-4">–†–µ–≤—é—Ç–∞</h3>

      {reviews.length === 0 ? (
        <p className="text-gray-500 text-center mb-4">–ù—è–º–∞ –Ω–∞–ª–∏—á–Ω–∏ —Ä–µ–≤—é—Ç–∞.</p>
      ) : (
        <div className="space-y-4 mb-6">
          {reviews.map((review, idx) => (
            <div key={idx} className="flex gap-3 items-start bg-white border border-gray-200 rounded-lg p-4 shadow hover:shadow-md transition">
              <div className="w-10 h-10 flex items-center justify-center rounded-full bg-blue-100 text-blue-700 font-semibold">
                {review.UserName?.charAt(0).toUpperCase()}
              </div>
              <div className="flex-1">
                <div className="flex justify-between items-center mb-1">
                  <span className="text-sm font-medium text-gray-800">
                    {review.UserName || "–ê–Ω–æ–Ω–∏–º–µ–Ω"}
                  </span>
                  <div className="flex items-center gap-2">
                    <span className="font-semibold text-sm">
                      {renderStars(review.Rating)}
                    </span>
                    {isAdmin && (
                      <button
                        onClick={() => handleDelete(review.ReviewID)}
                        className="text-red-600 hover:underline text-xs ml-2"
                        title="–ò–∑—Ç—Ä–∏–π —Ä–µ–≤—é"
                      >
                        üóëÔ∏è
                      </button>
                    )}
                  </div>
                </div>
                <p className="text-gray-700 text-sm italic">
                  ‚Äú{review.ReviewText}‚Äù
                </p>
              </div>
            </div>
          ))}
        </div>
      )}

      {user && (
        <div className="border-t pt-4 mt-6 bg-gray-50 p-4 rounded-lg">
          <h4 className="font-semibold mb-4 text-center text-gray-800 text-lg">–î–æ–±–∞–≤–∏ —Ä–µ–≤—é</h4>
          <div className="space-y-3">
            <textarea
              value={reviewText}
              onChange={(e) => setReviewText(e.target.value)}
              placeholder="–ö–∞–∫–≤–æ –º–∏—Å–ª–∏—à –∑–∞ —Ç–æ–∑–∏ –ø—Ä–æ–¥—É–∫—Ç?"
              className="w-full border border-gray-300 rounded-lg p-3 focus:outline-none focus:ring-2 focus:ring-blue-500 resize-none min-h-[100px]"
            />
            <div className="flex items-center gap-2">
              <label className="text-sm font-medium text-gray-700">–û—Ü–µ–Ω–∫–∞:</label>
              {Array.from({ length: 5 }, (_, i) => (
                <button
                  key={i}
                  type="button"
                  onClick={() => setRating(i + 1)}
                  className={i < rating ? "text-yellow-500 text-xl" : "text-gray-300 text-xl"}
                >
                  ‚òÖ
                </button>
              ))}
            </div>
            <button
              onClick={handleSubmit}
              className="w-full bg-blue-600 text-white px-4 py-2 rounded-lg font-semibold hover:bg-blue-700 transition"
            >
              –ò–∑–ø—Ä–∞—Ç–∏ —Ä–µ–≤—é
            </button>
          </div>
        </div>
      )}
    </div>
  );
}

export default ReviewSection;
