import React from "react";

export default function Hero() {
  return (
    <>
      {/* Hero Section */}
      <section className="w-full min-h-screen bg-blue-700 text-white flex flex-col justify-center items-center text-center px-4 pt-24">
        <h1 className="text-4xl md:text-6xl font-extrabold mb-4">
          Compare the Best Antivirus Software of 2025
        </h1>
        <p className="text-lg md:text-xl mb-8 max-w-2xl">
          Independent expert reviews to help you choose the right protection.
        </p>
        <a
          href="#top-picks"
          className="bg-white text-blue-700 px-6 py-3 rounded-full text-lg font-semibold hover:bg-gray-100 transition"
        >
          View Top Picks
        </a>
      

      {/* Info Cards Section - Solid blue */}
      <section className=" text-white py-20 px-4 -mt-16">
        <div className="max-w-7xl mx-auto grid grid-cols-1 md:grid-cols-3 gap-8 text-center">
          {[
            {
              icon: "🛡️",
              title: "Comprehensive Protection",
              desc: "We evaluate how effectively each solution protects against malware, ransomware, and phishing attacks.",
            },
            {
              icon: "⚙️",
              title: "Performance Impact",
              desc: "We measure how each antivirus affects your system’s speed during scans and real-time protection.",
            },
            {
              icon: "🔒",
              title: "Additional Features",
              desc: "We evaluate extras like VPNs, password managers, and parental controls.",
            },
          ].map((card, i) => (
            <div
              key={i}
              className="bg-blue-900 p-6 rounded-xl shadow-md hover:shadow-lg transition duration-300"
            >
              <div className="text-4xl mb-4">{card.icon}</div>
              <h3 className="text-xl font-semibold mb-2">{card.title}</h3>
              <p className="text-sm">{card.desc}</p>
            </div>
          ))}
        </div>
  </section>
      </section>
    </>
  );
}
