// ✅ Admin Panel с две колони: форма в дясно, таблица в ляво
import React, { useEffect, useState } from "react";
import Navbar from "./components/Navbar";
import "./index.css";

function AdminPageT() {
  const [products, setProducts] = useState([]);
  const [name, setName] = useState("");
  const [description, setDescription] = useState("");
  const [price, setPrice] = useState("");
  const [image, setImage] = useState("");
  const [fileName, setFileName] = useState("");
  const [features, setFeatures] = useState("");
  const [editingId, setEditingId] = useState(null);

  useEffect(() => {
    fetchProducts();
  }, []);

  const fetchProducts = async () => {
    try {
      const res = await fetch("http://localhost:5000/api/admin");
      const data = await res.json();
      setProducts(Array.isArray(data) ? data : []);
    } catch (err) {
      console.error("❌ Грешка при зареждане:", err);
    }
  };

  const convertToBase64 = (file) => {
    return new Promise((resolve, reject) => {
      const reader = new FileReader();
      reader.readAsDataURL(file);
      reader.onload = () => resolve(reader.result);
      reader.onerror = (error) => reject(error);
    });
  };

  const handleImageChange = async (e) => {
    const file = e.target.files[0];
    if (file) {
      const base64 = await convertToBase64(file);
      setImage(base64);
      setFileName(file.name);
    }
  };

  const handleAdd = async () => {
    if (!name.trim() || !description.trim() || !price.trim() || !image.trim()) {
      alert("Моля, попълнете всички полета.");
      return;
    }

    const method = editingId ? "PUT" : "POST";
    const url = editingId
      ? `http://localhost:5000/api/admin/${editingId}`
      : "http://localhost:5000/api/admin";

    try {
      const res = await fetch(url, {
        method,
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ name, description, image, price }),
      });

      const text = await res.text();
      let data;
      try {
        data = JSON.parse(text);
      } catch {
        alert("Сървърът върна неочакван отговор.");
        return;
      }

      if (res.ok) {
        alert(data.message);
        setName("");
        setDescription("");
        setPrice("");
        setImage("");
        setFileName("");
        setFeatures("");
        setEditingId(null);
        fetchProducts();
      } else {
        alert("Грешка при заявката");
      }
    } catch (err) {
      console.error("❌ Грешка при заявката:", err);
    }
  };

  const handleDelete = async (id) => {
    try {
      const res = await fetch(`http://localhost:5000/api/admin/${id}`, {
        method: "DELETE",
      });

      const data = await res.json();
      alert(data.message);
      fetchProducts();
    } catch (err) {
      console.error("❌ Грешка при изтриване:", err);
    }
  };

  const handleFeatureSave = async () => {
    if (!editingId) return;
    try {
      const res = await fetch(`http://localhost:5000/api/admin/${editingId}/features`, {
        method: "PUT",
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer ${localStorage.getItem("token")}`,
        },
        body: JSON.stringify({ features }),
      });

      const data = await res.json();
      alert(data.message);
      setFeatures("");
      setEditingId(null);
      fetchProducts();
    } catch (err) {
      console.error("❌ Грешка при записване на features:", err);
      alert("Неуспешно записване на Key Features.");
    }
  };

  return (
    <div className="admin-page">
      <Navbar />
      <div className="flex flex-col lg:flex-row gap-6 p-6">
        {/* Таблица отляво */}
        <div className="w-full lg:w-2/3">
          <h2 className="text-xl font-bold mb-4">Продукти</h2>
          <div className="product-table overflow-x-auto">
            <table className="min-w-full">
              <thead>
                <tr className="bg-gray-200">
                  <th className="p-2">Снимка</th>
                  <th>Име</th>
                  <th>Описание</th>
                  <th>Цена</th>
                  <th>Действие</th>
                </tr>
              </thead>
              <tbody>
                {products.map((p) => (
                  <tr key={p.ProductID} className="border-b">
                    <td><img src={p.ImageURL} alt={p.ProductName} style={{ width: 50 }} /></td>
                    <td>{p.ProductName}</td>
                    <td>{p.Description}</td>
                    <td>{p.Price}</td>
                    <td className="flex flex-col gap-2">
                      <button className="edit-button" onClick={() => {
                        setEditingId(p.ProductID);
                        setName(p.ProductName);
                        setDescription(p.Description);
                        setPrice(p.Price);
                        setImage(p.ImageURL);
                        setFeatures(p.KeyFeatures || "");
                      }}>Редактирай</button>
                      <button className="delete-button" onClick={() => handleDelete(p.ProductID)}>Изтрий</button>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        </div>

        {/* Форма отдясно */}
        <div className="w-full lg:w-1/3 bg-white rounded-xl shadow-md p-4">
          <h2 className="text-lg font-semibold mb-2">{editingId ? "Редакция на продукт" : "Добавяне на продукт"}</h2>
          <input type="text" placeholder="Име" value={name} onChange={(e) => setName(e.target.value)} className="input mb-2" />
          <input type="text" placeholder="Описание" value={description} onChange={(e) => setDescription(e.target.value)} className="input mb-2" />
          <input type="text" placeholder="Цена" value={price} onChange={(e) => setPrice(e.target.value)} className="input mb-2" />
          <label htmlFor="fileUpload" className="custom-file-upload">Изображение</label>
          <input id="fileUpload" type="file" accept="image/*" onChange={handleImageChange} style={{ display: "none" }} />
          {fileName && <p className="text-sm">Избран файл: {fileName}</p>}
          <button onClick={handleAdd} className="btn-blue w-full mt-2">{editingId ? "Запази" : "Добави"}</button>

          {editingId && (
            <div className="mt-4">
              <h3 className="font-semibold mb-1">Редакция на Key Features</h3>
              <textarea
                className="textarea"
                value={features}
                onChange={(e) => setFeatures(e.target.value)}
                placeholder="Въведи Key Features по редове..."
              />
              <button onClick={handleFeatureSave} className="btn-blue mt-2">💾 Запиши Key Features</button>
            </div>
          )}
        </div>
      </div>
    </div>
  );
}

export default AdminPageT;
