import React from "react";

export default function FAQSection() {
  const faqs = [
    {
      question: "Do I really need antivirus software in 2025?",
      answer:
        "Yes, even in 2025, antivirus software is essential to protect against evolving malware, ransomware, and phishing attacks.",
    },
    {
      question: "What's the difference between free and paid antivirus software?",
      answer:
        "Free antivirus offers basic protection, while paid versions include advanced features like real-time scanning, firewall, and customer support.",
    },
    {
      question: "Will antivirus software slow down my computer?",
      answer:
        "Modern antivirus software is optimized for performance and typically has minimal impact on system speed.",
    },
    {
      question: "Can I use multiple antivirus programs for better protection?",
      answer:
        "It's not recommended. Running multiple antivirus programs can cause conflicts and reduce overall effectiveness.",
    },
    {
      question: "How often should I run an antivirus scan?",
      answer:
        "You should schedule regular scans weekly, and ensure real-time protection is always enabled.",
    },
    {
      question: "Is Windows Defender good enough for basic protection?",
      answer:
        "Windows Defender offers decent protection, but third-party antivirus solutions often provide more advanced features.",
    },
  ];

  return (
    <section id="faq" className="bg-gray-100 py-16">
      <div className="max-w-3xl mx-auto text-center">
        <h2 className="text-2xl font-bold text-blue-800 mb-2">
          Frequently Asked Questions
        </h2>
        <p className="text-gray-600 text-sm mb-10">
          Common questions about antivirus software and computer security.
        </p>

        <div className="bg-white shadow rounded-lg text-left divide-y divide-gray-200">
          {faqs.map((faq, index) => (
            <details key={index} className="group px-6 py-4 cursor-pointer">
              <summary className="font-medium text-gray-800 group-open:text-blue-700">
                {faq.question}
              </summary>
              <p className="mt-2 text-gray-600 text-sm">{faq.answer}</p>
            </details>
          ))}
        </div>

        <div className="bg-blue-600 text-white mt-10 p-6 rounded-lg">
          <h4 className="font-semibold text-lg mb-2">Still have questions?</h4>
          <p className="text-sm mb-4">
            Our security experts are ready to help you find the right antivirus solution for your specific needs.
          </p>
          <button className="bg-white text-blue-600 font-semibold px-4 py-2 rounded hover:bg-blue-100 transition">
            Ask an Expert
          </button>
        </div>
      </div>
    </section>
  );
}
