import React from "react";
import { useNavigate } from "react-router-dom";

function AntivirusListT({ products = [] }) {
  const navigate = useNavigate();

  if (products.length === 0) {
    return React.createElement(
      "div",
      { className: "loading", style: { padding: "2rem", textAlign: "center" } },
      "Зареждане на продукти..."
    );
  }

  return React.createElement(
    "div",
    { className: "antivirus-list" },
    [
      React.createElement("h2", { className: "title", key: "title" }, "Нашите Антивирусни Предложения"),
      React.createElement(
        "div",
        { className: "product-row", key: "product-row" },
        products.map((product) =>
          React.createElement(
            "div",
            { className: "product-card", key: product.ProductID },
            [
              React.createElement("img", {
                src: product.ImageURL,
                alt: product.ProductName,
                className: "product-img",
                key: "img-" + product.ProductID,
              }),
              React.createElement("h3", { className: "product-name", key: "name-" + product.ProductID }, product.ProductName),
              React.createElement("p", { className: "description", key: "desc-" + product.ProductID }, product.Description),
              React.createElement("p", { className: "price", key: "price-" + product.ProductID }, `${product.Price} лв.`),
              React.createElement(
                "button",
                {
                  className: "review-button",
                  key: "btn-" + product.ProductID,
                  onClick: () => navigate(`/product/${product.ProductID}`)
                },
                "Прочети ревю"
              ),
            ]
          )
        )
      ),
    ]
  );
}

export default AntivirusListT;
