import React, { useEffect, useState } from "react";
import { useParams, useNavigate } from "react-router-dom";

function ProductDetails() {
  const { id } = useParams();
  const [product, setProduct] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    fetch(`http://localhost:5000/api/products/${id}`)
      .then((res) => {
        if (!res.ok) throw new Error("Продуктът не е намерен");
        return res.json();
      })
      .then((data) => setProduct(data))
      .catch(() => navigate("/")); // Връщане към началната, ако няма продукт
  }, [id, navigate]);

  if (!product) return <div>Зареждане...</div>;

  return (
    <div className="product-details">
      <button onClick={() => navigate(-1)}>← Назад</button>
      <h2>{product.ProductName}</h2>
      <img src={product.ImageURL} alt={product.ProductName} />
      <p>{product.Description}</p>
      <p><strong>Цена:</strong> {product.Price} лв.</p>
      {/* По-късно може да добавим рейтинг и ревюта */}
    </div>
  );
}

export default ProductDetails;
