import React, { useEffect, useState, useRef } from "react";
import { Link, useNavigate } from "react-router-dom";
import { Search } from "lucide-react";

export default function Navbar() {
  const [isAdmin, setIsAdmin] = useState(false);
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [products, setProducts] = useState([]);
  const [searchTerm, setSearchTerm] = useState("");
  const navigate = useNavigate();
  const searchRef = useRef(null);

  useEffect(() => {
    const adminStatus = localStorage.getItem("isAdmin");
    const token = localStorage.getItem("token");
    setIsAdmin(adminStatus === "true");
    setIsLoggedIn(!!token);

    fetch("http://localhost:5000/api/admin")
      .then((res) => res.json())
      .then((data) => setProducts(Array.isArray(data) ? data : []));
  }, []);

  useEffect(() => {
    const handleClickOutside = (event) => {
      if (searchRef.current && !searchRef.current.contains(event.target)) {
        setSearchTerm(""); // затваряне на dropdown
      }
    };
    document.addEventListener("mousedown", handleClickOutside);
    return () => {
      document.removeEventListener("mousedown", handleClickOutside);
    };
  }, []);

  const handleLogout = () => {
    localStorage.removeItem("token");
    localStorage.removeItem("isAdmin");
    navigate("/");
    window.location.reload();
  };

  const filtered = searchTerm
    ? products.filter((p) =>
        (p.ProductName?.toLowerCase() + " " + p.Description?.toLowerCase()).includes(searchTerm.toLowerCase())
      )
    : [];

  return (
    <nav className="w-full bg-white shadow-md fixed top-0 left-0 z-50">
      <div className="max-w-7xl mx-auto px-4 py-3 flex items-center justify-between">
        {/* Logo */}
        <h1 className="text-xl font-bold text-blue-800">
          <Link to="/" className="hover:underline">🛡️ SecureGuard</Link>
        </h1>

        {/* Right Side */}
        <div className="flex items-center gap-4 relative">
          <Link to="/compare" className="text-gray-600 hover:text-blue-600 font-medium">Compare</Link>
          <a href="#top-picks" className="text-gray-600 hover:text-blue-600 font-medium">Top Picks</a>
          <a href="#expert-reviews" className="text-gray-600 hover:text-blue-600 font-medium">Expert Reviews</a>
          <a href="#faq" className="text-gray-600 hover:text-blue-600 font-medium">FAQ</a>

          {/* Search with hover-expand and blur-close */}
          <div className="relative group" ref={searchRef}>
            <div className="flex items-center border border-gray-300 rounded px-2 py-1 bg-white h-9 w-9 group-hover:w-60 focus-within:w-60 transition-all duration-300 overflow-hidden">
              <Search size={16} className="text-gray-400 shrink-0" />
              <input
                type="text"
                placeholder="Search..."
                className="ml-2 w-full text-sm focus:outline-none opacity-0 group-hover:opacity-100 focus:opacity-100 transition-opacity duration-200"
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
                onKeyDown={(e) => {
                  if (e.key === "Enter" && filtered.length > 0) {
                    navigate(`/product/${encodeURIComponent(filtered[0].ProductName)}`);
                    setSearchTerm("");
                  }
                }}
              />
            </div>

            {filtered.length > 0 && (
              <div className="absolute left-0 right-0 mt-1 bg-white border border-gray-200 rounded shadow z-50 max-h-60 overflow-y-auto">
                {filtered.map((product) => (
                  <div
                    key={product.ProductID}
                    className="px-4 py-2 hover:bg-blue-100 cursor-pointer text-sm flex justify-between"
                    onClick={() => {
                      navigate(`/product/${encodeURIComponent(product.ProductName)}`);
                      setSearchTerm("");
                    }}
                  >
                    <span>{product.ProductName}</span>
                    <span className="text-blue-600 font-medium">{product.Price}</span>
                  </div>
                ))}
              </div>
            )}
          </div>

          {/* Login/Admin */}
          {isAdmin && (
            <Link to="/admin" className="px-4 py-2 border border-green-600 text-green-600 rounded hover:bg-green-600 hover:text-white transition font-medium">
              Админ Панел
            </Link>
          )}

          {isLoggedIn ? (
            <button
              onClick={handleLogout}
              className="px-4 py-2 border border-red-600 text-red-600 rounded hover:bg-red-600 hover:text-white transition font-medium"
            >
              Изход
            </button>
          ) : (
            <Link
              to="/login"
              className="px-4 py-2 border border-blue-600 text-blue-600 rounded hover:bg-blue-600 hover:text-white transition font-medium"
            >
              Вписване
            </Link>
          )}
        </div>
      </div>
    </nav>
  );
}
