import React, { useState, useEffect } from "react";
import Navbar from "./components/Navbar";

function AdminPage() {
  const [products, setProducts] = useState([]);
  const [name, setName] = useState("");
  const [description, setDescription] = useState("");
  const [price, setPrice] = useState("");

  // Зареждаме продуктите при първо зареждане
  useEffect(() => {
    fetchProducts();
  }, []);

  const fetchProducts = async () => {
    try {
      const res = await fetch("http://localhost:5000/api/products");
      const data = await res.json();
      setProducts(data);
    } catch (err) {
      console.error("Грешка при зареждане на продукти:", err);
    }
  };

  const handleAdd = async () => {
    try {
      const res = await fetch("http://localhost:5000/api/products", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ name, description, price }),
      });

      const data = await res.json();
      alert(data.message || "Продуктът е добавен!");

      setName("");
      setDescription("");
      setPrice("");
      fetchProducts(); // презареждаме списъка
    } catch (err) {
      console.error("Грешка при добавяне:", err);
    }
  };

  const handleDelete = async (id) => {
    try {
      const res = await fetch(`http://localhost:5000/api/products/${id}`, {
        method: "DELETE",
      });

      const data = await res.json();
      alert(data.message || "Продуктът е изтрит!");
      fetchProducts();
    } catch (err) {
      console.error("Грешка при изтриване:", err);
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-r from-purple-500 to-pink-500">
      <Navbar />

      {/* Форма за добавяне */}
      <div className="pt-32 px-4 flex justify-center">
        <div className="bg-white shadow-2xl rounded-xl p-8 w-full max-w-lg animate-fade-in">
          <h2 className="text-2xl font-bold text-center mb-6 text-gray-800">
            Добавяне на продукт
          </h2>

          <input
            type="text"
            placeholder="Име"
            value={name}
            onChange={(e) => setName(e.target.value)}
            className="w-full mb-4 px-4 py-2 border border-gray-300 rounded focus:outline-none focus:ring-2 focus:ring-purple-500"
          />

          <input
            type="text"
            placeholder="Описание"
            value={description}
            onChange={(e) => setDescription(e.target.value)}
            className="w-full mb-4 px-4 py-2 border border-gray-300 rounded focus:outline-none focus:ring-2 focus:ring-purple-500"
          />

          <input
            type="number"
            placeholder="Цена"
            value={price}
            onChange={(e) => setPrice(e.target.value)}
            className="w-full mb-4 px-4 py-2 border border-gray-300 rounded focus:outline-none focus:ring-2 focus:ring-purple-500"
          />

          <button
            onClick={handleAdd}
            className="w-full bg-blue-600 text-white font-semibold py-2 px-4 rounded hover:bg-blue-700 transition"
          >
            Добави
          </button>
        </div>
      </div>

      {/* Списък с продукти */}
      <div className="mt-10 px-4 max-w-5xl mx-auto">
        <h3 className="text-xl font-bold mb-4 text-white">Продукти:</h3>
        {products.length === 0 ? (
          <p className="text-white">Няма добавени продукти.</p>
        ) : (
          <div className="bg-white shadow-md rounded-lg overflow-x-auto">
            <table className="min-w-full">
              <thead className="bg-gray-100">
                <tr>
                  <th className="py-2 px-4 text-left">Име</th>
                  <th className="py-2 px-4 text-left">Описание</th>
                  <th className="py-2 px-4 text-left">Цена</th>
                  <th className="py-2 px-4 text-left">Действие</th>
                </tr>
              </thead>
              <tbody>
                {products.map((product) => (
                  <tr key={product.id} className="border-t">
                    <td className="py-2 px-4">{product.name}</td>
                    <td className="py-2 px-4">{product.description}</td>
                    <td className="py-2 px-4">{product.price} лв.</td>
                    <td className="py-2 px-4">
                      <button
                        onClick={() => handleDelete(product.id)}
                        className="bg-red-600 text-white px-3 py-1 rounded hover:bg-red-700 transition"
                      >
                        Изтрий
                      </button>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        )}
      </div>
    </div>
  );
}

export default AdminPage;
