import React, { useEffect, useState } from 'react';

function AdminPage() {
  const [products, setProducts] = useState([]);
  const [form, setForm] = useState({ name: '', description: '', price: '' });

  const token = localStorage.getItem('token');

  useEffect(() => {
    fetch('/api/admin/products', {
      headers: { Authorization: `Bearer ${token}` }
    })
      .then(res => res.json())
      .then(data => setProducts(data));
  }, []);

  const handleAdd = async (e) => {
    e.preventDefault();
    await fetch('/api/admin/products', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
        Authorization: `Bearer ${token}`
      },
      body: JSON.stringify(form)
    });
    window.location.reload();
  };

  const handleDelete = async (id) => {
    await fetch(`/api/admin/products/${id}`, {
      method: 'DELETE',
      headers: { Authorization: `Bearer ${token}` }
    });
    window.location.reload();
  };

  return (
    <div>
      <h1>Админ Панел</h1>
      <form onSubmit={handleAdd}>
        <input placeholder="Име" onChange={(e) => setForm({ ...form, name: e.target.value })} required />
        <input placeholder="Описание" onChange={(e) => setForm({ ...form, description: e.target.value })} required />
        <input type="number" placeholder="Цена" onChange={(e) => setForm({ ...form, price: e.target.value })} required />
        <button type="submit">Добави</button>
      </form>
      <ul>
        {products.map(p => (
          <li key={p.ProductID}>
            {p.Name} - {p.Price} лв.
            <button onClick={() => handleDelete(p.ProductID)}>Изтрий</button>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default AdminPage;
