// ✅ Обновен AdminPageTemp.js с Accordion стил
import React, { useState, useEffect } from "react";
import "./index.css";

function AdminPageTemp() {
  const [products, setProducts] = useState([]);
  const [selectedProduct, setSelectedProduct] = useState(null);

  // Основна информация
  const [name, setName] = useState("");
  const [description, setDescription] = useState("");
  const [price, setPrice] = useState("");
  const [image, setImage] = useState("");

  // Key Features
  const [keyFeatures, setKeyFeatures] = useState("");

  // Expert Review
  const [ourReview, setOurReview] = useState("");
  const [pros, setPros] = useState("");
  const [cons, setCons] = useState("");
  const [bottomLine, setBottomLine] = useState("");

  // Test Results
  const [malware, setMalware] = useState("");
  const [performance, setPerformance] = useState("");
  const [ui, setUI] = useState("");
  const [value, setValue] = useState("");

  // Protection Features
  const [protectionFeatures, setProtectionFeatures] = useState("");

  // Accordion state
  const [openSection, setOpenSection] = useState(null);

  const toggleSection = (section) => {
    setOpenSection(openSection === section ? null : section);
  };

  // Example fetch (зареждане на продукти)
  useEffect(() => {
    fetch("http://localhost:5000/api/admin/products")
      .then((res) => res.json())
      .then((data) => setProducts(data))
      .catch((err) => console.error("Грешка при зареждане:", err));
  }, []);

  return (
    <div className="p-6 max-w-4xl mx-auto space-y-6">
      <h2 className="text-2xl font-bold text-center text-white">Админ Панел</h2>

      {/* Секция 1 */}
      <div className="accordion bg-white rounded shadow-md">
        <button className="w-full text-left p-4 font-semibold" onClick={() => toggleSection("main")}>Основна информация</button>
        {openSection === "main" && (
          <div className="p-4 border-t space-y-2">
            <input className="input" placeholder="Име" value={name} onChange={(e) => setName(e.target.value)} />
            <input className="input" placeholder="Описание" value={description} onChange={(e) => setDescription(e.target.value)} />
            <input className="input" placeholder="Цена" value={price} onChange={(e) => setPrice(e.target.value)} />
            <input className="input" placeholder="URL на изображение" value={image} onChange={(e) => setImage(e.target.value)} />
            <button className="btn-blue">Запази</button>
          </div>
        )}
      </div>

      {/* Секция 2 */}
      <div className="accordion bg-white rounded shadow-md">
        <button className="w-full text-left p-4 font-semibold" onClick={() => toggleSection("key")}>Key Features</button>
        {openSection === "key" && (
          <div className="p-4 border-t">
            <textarea className="textarea" value={keyFeatures} onChange={(e) => setKeyFeatures(e.target.value)} />
            <button className="btn-blue mt-2">Запиши Key Features</button>
          </div>
        )}
      </div>

      {/* Секция 3 */}
      <div className="accordion bg-white rounded shadow-md">
        <button className="w-full text-left p-4 font-semibold" onClick={() => toggleSection("review")}>Expert Review</button>
        {openSection === "review" && (
          <div className="p-4 border-t space-y-2">
            <textarea className="textarea" placeholder="Our Review" value={ourReview} onChange={(e) => setOurReview(e.target.value)} />
            <textarea className="textarea" placeholder="Pros (разделени с нов ред)" value={pros} onChange={(e) => setPros(e.target.value)} />
            <textarea className="textarea" placeholder="Cons (разделени с нов ред)" value={cons} onChange={(e) => setCons(e.target.value)} />
            <textarea className="textarea" placeholder="Bottom Line" value={bottomLine} onChange={(e) => setBottomLine(e.target.value)} />
            <button className="btn-blue">Запиши Expert Review</button>
          </div>
        )}
      </div>

      {/* Секция 4 */}
      <div className="accordion bg-white rounded shadow-md">
        <button className="w-full text-left p-4 font-semibold" onClick={() => toggleSection("tests")}>Test Results</button>
        {openSection === "tests" && (
          <div className="p-4 border-t grid grid-cols-2 gap-2">
            <input className="input" placeholder="Malware Protection" value={malware} onChange={(e) => setMalware(e.target.value)} />
            <input className="input" placeholder="Performance Impact" value={performance} onChange={(e) => setPerformance(e.target.value)} />
            <input className="input" placeholder="User Interface" value={ui} onChange={(e) => setUI(e.target.value)} />
            <input className="input" placeholder="Value for Money" value={value} onChange={(e) => setValue(e.target.value)} />
            <button className="btn-blue col-span-2">Запиши Test Results</button>
          </div>
        )}
      </div>

      {/* Секция 5 */}
      <div className="accordion bg-white rounded shadow-md">
        <button className="w-full text-left p-4 font-semibold" onClick={() => toggleSection("features")}>Protection Features</button>
        {openSection === "features" && (
          <div className="p-4 border-t">
            <textarea className="textarea" placeholder="Protection features, всяка на нов ред" value={protectionFeatures} onChange={(e) => setProtectionFeatures(e.target.value)} />
            <button className="btn-blue mt-2">Запиши Protection Features</button>
          </div>
        )}
      </div>
    </div>
  );
}

export default AdminPageTemp;
