import React, { useEffect, useState } from "react";
import "./index.css";

function AdminPage() {
  const [products, setProducts] = useState([]);
  const [name, setName] = useState("");
  const [description, setDescription] = useState("");
  const [price, setPrice] = useState("");
  const [image, setImage] = useState("");

  useEffect(() => {
    fetchProducts();
  }, []);

  const fetchProducts = async () => {
    try {
      const res = await fetch("http://localhost:5000/api/admin");
      const data = await res.json();
      setProducts(Array.isArray(data) ? data : []);
    } catch (err) {
      console.error("❌ Error fetching products:", err);
    }
  };

  const handleAdd = async () => {
    if (!name || !description || !price || !image) return alert("All fields required");

    try {
      await fetch("http://localhost:5000/api/admin", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ name, description, price, imageURL: image }),
      });
      setName("");
      setDescription("");
      setPrice("");
      setImage("");
      fetchProducts();
    } catch (err) {
      console.error("❌ Error adding product:", err);
    }
  };

  const handleDelete = async (id) => {
    try {
      await fetch(`http://localhost:5000/api/admin/${id}`, { method: "DELETE" });
      fetchProducts();
    } catch (err) {
      console.error("❌ Error deleting product:", err);
    }
  };

  const toBase64 = (file) =>
    new Promise((resolve, reject) => {
      const reader = new FileReader();
      reader.readAsDataURL(file);
      reader.onload = () => resolve(reader.result);
      reader.onerror = (error) => reject(error);
    });

  const handleImageUpload = async (e) => {
    const file = e.target.files[0];
    if (!file) return;
    const base64 = await toBase64(file);
    setImage(base64);
  };

  return (
    <div className="min-h-screen bg-gradient-to-r from-purple-500 to-pink-500 py-16 px-4">
      <div className="max-w-xl mx-auto bg-white rounded-xl p-6 shadow-lg">
        <h2 className="text-xl font-bold text-center mb-4">Добавяне на продукт</h2>
        <input
          type="text"
          placeholder="Име"
          value={name}
          onChange={(e) => setName(e.target.value)}
          className="w-full mb-3 p-2 border rounded"
        />
        <input
          type="text"
          placeholder="Описание"
          value={description}
          onChange={(e) => setDescription(e.target.value)}
          className="w-full mb-3 p-2 border rounded"
        />
        <input
          type="text"
          placeholder="Цена"
          value={price}
          onChange={(e) => setPrice(e.target.value)}
          className="w-full mb-3 p-2 border rounded"
        />
        <input
          type="file"
          accept="image/*"
          onChange={handleImageUpload}
          className="w-full mb-4"
        />
        <button
          onClick={handleAdd}
          className="w-full bg-blue-600 text-white py-2 rounded hover:bg-blue-700"
        >
          Добави
        </button>
      </div>

      <div className="max-w-4xl mx-auto mt-12 bg-white rounded-xl p-6 shadow-xl overflow-x-auto">
        <table className="w-full table-auto">
          <thead>
            <tr className="bg-gray-200 text-left text-sm">
              <th className="p-2">Снимка</th>
              <th className="p-2">Име</th>
              <th className="p-2">Описание</th>
              <th className="p-2">Цена</th>
              <th className="p-2 text-center">Действие</th>
            </tr>
          </thead>
          <tbody>
            {products.map((p) => (
              <tr key={p.ProductID} className="border-t text-sm">
                <td className="p-2">
                  <img src={p.ImageURL} alt="preview" className="h-10 object-contain" />
                </td>
                <td className="p-2 font-semibold">{p.ProductName}</td>
                <td className="p-2">{p.Description}</td>
                <td className="p-2">{p.Price}</td>
                <td className="p-2 text-center">
                  <button className="bg-yellow-400 text-white px-3 py-1 rounded mr-2 hover:bg-yellow-500">
                    Редактирай
                  </button>
                  <button
                    onClick={() => handleDelete(p.ProductID)}
                    className="bg-red-600 text-white px-3 py-1 rounded hover:bg-red-700"
                  >
                    Изтрий
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
}

export default AdminPage;
