import React, { useEffect, useState } from "react";
import { useParams, useNavigate } from "react-router-dom";
import ReviewSection from "./components/ReviewSection";

function ProductDetails() {
  const { name } = useParams(); // Идва от /product/:name
  const [product, setProduct] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    if (!name) return;

    fetch(`http://localhost:5000/api/admin/products/${name}`)
      .then((res) => {
        if (!res.ok) throw new Error("Продуктът не е намерен");
        return res.json();
      })
      .then((data) => setProduct(data))
      .catch((err) => {
        console.error("❌ Грешка при зареждане:", err);
        navigate("/"); // Пренасочване при грешка
      });
  }, [name, navigate]);

  if (!product)
    return <div style={{ padding: "2rem", textAlign: "center" }}>Зареждане...</div>;

  return (
    <div className="product-details p-6 max-w-2xl mx-auto text-center">
      <button
        className="text-blue-600 underline mb-4"
        onClick={() => navigate(-1)}
      >
        ← Назад
      </button>
      <h2 className="text-2xl font-bold">{product.ProductName}</h2>
      <img
        src={product.ImageURL}
        alt={product.ProductName}
        className="h-32 mx-auto my-4"
      />
      <p className="text-gray-700">{product.Description}</p>
      <p className="font-semibold text-lg mt-2">{product.Price} лв.</p>
      <ReviewSection productId={product.ProductID} />
    </div>
  );
}

export default ProductDetails;
