import React, { useEffect, useState } from "react";
function ReviewSection({ productId }) {
  const [reviews, setReviews] = useState([]);
  const [reviewText, setReviewText] = useState("");
  const [rating, setRating] = useState(5);
  const [user, setUser] = useState(null);

  useEffect(() => {
    const token = localStorage.getItem("token");
    const userData = token ? JSON.parse(atob(token.split(".")[1])) : null;
    setUser(userData);

    fetch(`http://localhost:5000/api/reviews/${productId}`)
      .then(res => res.json())
      .then(data => setReviews(data));
  }, [productId]);

  const handleSubmit = async () => {
    if (!user) return;

    const token = localStorage.getItem("token");
    const response = await fetch("http://localhost:5000/api/reviews", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
        Authorization: "Bearer " + token,
      },
      body: JSON.stringify({ productId, reviewText, rating }),
    });

    if (response.ok) {
      const newReview = { reviewText, rating, UserID: user.id };
      setReviews([...reviews, newReview]);
      setReviewText("");
      setRating(5);
    }
  };

  const handleDelete = async (reviewId) => {
    const token = localStorage.getItem("token");
    const response = await fetch(`http://localhost:5000/api/reviews/${reviewId}`, {
      method: "DELETE",
      headers: {
        Authorization: "Bearer " + token,
      },
    });

    if (response.ok) {
      setReviews(reviews.filter(r => r.ReviewID !== reviewId));
    }
  };

  return (
    <div className="p-4 border-t mt-4">
      <h3 className="text-lg font-semibold mb-2">Ревюта</h3>
      {reviews.map((rev, index) => (
        <div key={index} className="border p-2 rounded mb-2">
          <p className="font-semibold">Оценка: {rev.Rating} ★</p>
          <p>{rev.ReviewText}</p>
          {user?.isAdmin && (
            <button onClick={() => handleDelete(rev.ReviewID)} className="text-red-600 text-sm mt-1">Изтрий</button>
          )}
        </div>
      ))}
      {user && (
        <div className="mt-4">
          <textarea value={reviewText} onChange={(e) => setReviewText(e.target.value)} />
          <select value={rating} onChange={(e) => setRating(Number(e.target.value))}>
            {[1, 2, 3, 4, 5].map(n => <option key={n} value={n}>{n}</option>)}
          </select>
          <button onClick={handleSubmit}>Добави ревю</button>
        </div>
      )}
    </div>
  );
}

export default ReviewSection;
