import React from "react";

function FAQSection() {
  const faqs = [
    {
      question: "Do I really need antivirus software in 2025?",
      answer:
        "Yes, antivirus software remains essential in 2025. While operating systems have improved their built-in security, specialized antivirus solutions offer comprehensive protection against sophisticated malware, ransomware, and zero-day exploits. They also provide additional security features like VPNs, password managers, and parental controls that aren't included in default OS protection."
    },
    {
      question: "What's the difference between free and paid antivirus software?",
      answer:
        "Free antivirus software provides basic protection against common threats. Paid solutions include advanced features like real-time protection, ransomware shields, VPNs, and priority support."
    },
    {
      question: "Will antivirus software slow down my computer?",
      answer:
        "Most modern antivirus solutions are optimized for performance. However, some may cause slowdowns during full system scans. Lightweight solutions and proper settings can minimize impact."
    },
    {
      question: "Can I use multiple antivirus programs for better protection?",
      answer:
        "It is not recommended to run multiple real-time antivirus programs simultaneously as they can conflict and reduce overall protection."
    },
    {
      question: "How often should I run an antivirus scan?",
      answer:
        "A weekly full system scan is recommended, in addition to always-on real-time protection."
    },
    {
      question: "Is Windows Defender good enough for basic protection?",
      answer:
        "Windows Defender offers solid baseline protection. However, dedicated antivirus software often provides better malware detection rates and more advanced features."
    }
  ];

  return (
    <section className="max-w-4xl mx-auto py-12 px-4">
      <h2 className="text-2xl font-bold text-center mb-2 text-blue-900">
        Frequently Asked Questions
      </h2>
      <p className="text-center text-gray-600 mb-6 text-sm">
        Common questions about antivirus software and computer security.
      </p>
      <div className="bg-white rounded shadow divide-y">
        {faqs.map((faq, idx) => (
          <details key={idx} className="px-6 py-4 hover:bg-gray-50 group">
            <summary className="cursor-pointer font-medium text-gray-800 group-hover:text-blue-600">
              {faq.question}
            </summary>
            <p className="mt-2 text-gray-600 text-sm leading-relaxed">
              {faq.answer}
            </p>
          </details>
        ))}
      </div>

      <div className="bg-blue-600 text-white text-center py-6 px-4 mt-10 rounded-lg">
        <h3 className="font-semibold text-lg mb-1">Still have questions?</h3>
        <p className="text-sm mb-4">
          Our security experts are ready to help you find the right antivirus solution for your specific needs.
        </p>
        <button className="bg-white text-blue-700 font-medium py-2 px-4 rounded hover:bg-gray-100">
          Ask an Expert
        </button>
      </div>
    </section>
  );
}

export default FAQSection;
