import React, { useEffect, useState } from "react";
import { useParams, useNavigate } from "react-router-dom";
import ReviewSection from "./components/ReviewSection";

function ProductDetails() {
  const { name } = useParams();
  const [product, setProduct] = useState(null);
  const [features, setFeatures] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    fetch(`http://localhost:5000/api/admin/name/${name}`)
      .then((res) => {
        if (!res.ok) throw new Error("–ü—Ä–æ–¥—É–∫—Ç—ä—Ç –Ω–µ –µ –Ω–∞–º–µ—Ä–µ–Ω");
        return res.json();
      })
      .then((data) => setProduct(data))
      .catch((err) => {
        console.error("‚ùå –ì—Ä–µ—à–∫–∞ –ø—Ä–∏ –∑–∞—Ä–µ–∂–¥–∞–Ω–µ:", err);
        navigate("/");
      });

    fetch(`http://localhost:5000/api/products/${name}/features`)
      .then((res) => res.json())
      .then((data) => setFeatures(data))
      .catch((err) => console.error("‚ùå –ì—Ä–µ—à–∫–∞ –ø—Ä–∏ –∑–∞—Ä–µ–∂–¥–∞–Ω–µ –Ω–∞ features:", err));
  }, [name, navigate]);

  if (!product) {
    return (
      <div className="p-8 text-center">–ó–∞—Ä–µ–∂–¥–∞–Ω–µ...</div>
    );
  }

  return (
    <>
      <div className="mb-4 px-6">
        <button
          onClick={() => navigate("/")}
          className="bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700 transition"
        >
          üè† –ù–∞—á–∞–ª–æ
        </button>
      </div>

      <div className="p-6 max-w-5xl mx-auto bg-white rounded-xl shadow-md mt-6">
        <div className="flex flex-col md:flex-row items-center md:items-start gap-6">
          <img
            src={product.ImageURL || "/placeholder.png"}
            alt={product.ProductName}
            className="w-32 h-32 object-contain"
          />
          <div className="flex-1">
            <h1 className="text-2xl font-bold text-gray-900 mb-2">{product.ProductName}</h1>
            <p className="text-gray-600 mb-4">{product.Description}</p>
            <p className="text-green-600 font-semibold text-lg mb-2">–¶–µ–Ω–∞: {product.Price} –ª–≤</p>

            {typeof product.AverageRating === "number" && !isNaN(product.AverageRating) && (
              <p className="text-yellow-500 font-medium">
                ‚≠ê {product.AverageRating.toFixed(1)} / 5.0
              </p>
            )}

            {features.length > 0 && (
              <div className="mt-4">
                <h3 className="text-md font-semibold mb-1">–û—Å–Ω–æ–≤–Ω–∏ —Ö–∞—Ä–∞–∫—Ç–µ—Ä–∏—Å—Ç–∏–∫–∏:</h3>
                <ul className="list-disc list-inside text-gray-700 text-sm">
                  {features.map((f, index) => (
                    <li key={index}>{f.Feature || f}</li>
                  ))}
                </ul>
              </div>
            )}
          </div>
        </div>

        <div className="mt-10">
          <ReviewSection productId={product.ProductID} />
        </div>
      </div>
    </>
  );
}

export default ProductDetails;
