import React, { useState } from "react";
import { useNavigate } from "react-router-dom";

function Login() {
  const [fullName, setFullName] = useState("");
  const [password, setPassword] = useState("");
  const navigate = useNavigate();

  const handleLogin = async (e) => 
    {
    e.preventDefault();
    try {
      const res = await fetch("http://localhost:5000/api/login", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ fullName, password }),
      });
      const data = await res.json();

      if (res.ok) {
        alert(data.message);
        if (data.user.isAdmin) {
          navigate("/admin");
        } else {
          navigate("/dashboard"); 
        }
      } else {
        alert(data.message);
      }
    } catch (err) {
      alert("Възникна грешка при входа.");
    }

    const data = await res.json();
    if (res.ok) {
  localStorage.setItem("token", data.token);
  localStorage.setItem("isAdmin", data.user.isAdmin);
  alert(data.message || "Успешен вход!");
  window.location.href = data.user.isAdmin ? "/admin" : "/";
} else {
  alert(data.message);
}

  };

  return (
    <div className="form-container">
      <h2>Вход</h2>
      <form onSubmit={handleLogin}>
        <input type="text" placeholder="Име" onChange={(e) => setFullName(e.target.value)} required />
        <input type="password" placeholder="Парола" onChange={(e) => setPassword(e.target.value)} required />
        <button type="submit">Вход</button>
      </form>
      <a href="/register">Нямате акаунт? Регистрирайте се</a>
    </div>
  );
}

export default Login;
