import React, { useEffect, useState } from "react";
import ReviewCard from "./components/ReviewCard";
import "./ReviewSection.css";

function ReviewSection({ productId }) {
  const [reviews, setReviews] = useState([]);
  const [reviewText, setReviewText] = useState("");
  const [rating, setRating] = useState(5);
  const [user, setUser] = useState(null);

  useEffect(() => {
    const token = localStorage.getItem("token");
    const userData = token ? JSON.parse(atob(token.split(".")[1])) : null;
    setUser(userData);

    fetch(`http://localhost:5000/api/reviews/${productId}`)
      .then((res) => res.json())
      .then((data) => setReviews(data));
  }, [productId]);

  const handleSubmit = async () => {
    if (!user) return;

    const token = localStorage.getItem("token");
    try {
      const response = await fetch("http://localhost:5000/api/reviews", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          Authorization: "Bearer " + token,
        },
        body: JSON.stringify({ productId, reviewText, rating }),
      });

      if (response.ok) {
        const refreshed = await fetch(`http://localhost:5000/api/reviews/${productId}`);
        const updatedReviews = await refreshed.json();
        setReviews(updatedReviews);
        setReviewText("");
        setRating(5);
      } else {
        const error = await response.json();
        alert("Грешка при добавяне на ревю.");
        console.error(error);
      }
    } catch (err) {
      console.error("❌ Грешка при заявката:", err);
    }
  };

  const handleDelete = async (reviewId) => {
    const token = localStorage.getItem("token");
    const response = await fetch(`http://localhost:5000/api/reviews/${reviewId}`, {
      method: "DELETE",
      headers: {
        Authorization: "Bearer " + token,
      },
    });

    if (response.ok) {
      setReviews(reviews.filter((r) => r.ReviewID !== reviewId));
    }
  };

  return (
    <div className="max-w-4xl mx-auto mt-10 p-4 bg-gray-50 rounded-xl shadow-md">
      <h2 className="text-2xl font-bold mb-4 text-center text-blue-700">Ревюта</h2>

      {reviews.map((rev) => (
        <ReviewCard
          key={rev.ReviewID}
          review={rev}
          isAdmin={user?.isAdmin}
          onDelete={handleDelete}
        />
      ))}

      {user && (
        <div className="mt-6">
          <textarea
            className="w-full border rounded p-2 mb-2"
            value={reviewText}
            onChange={(e) => setReviewText(e.target.value)}
            placeholder="Напиши ревю..."
          />
          <select
            className="w-full border rounded p-2 mb-2"
            value={rating}
            onChange={(e) => setRating(Number(e.target.value))}
          >
            {[1, 2, 3, 4, 5].map((n) => (
              <option key={n} value={n}>
                {n}
              </option>
            ))}
          </select>
          <button
            onClick={handleSubmit}
            className="bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700 transition"
          >
            Добави ревю
          </button>
        </div>
      )}
    </div>
  );
}

export default ReviewSection;
