import React from "react";

const faqs = [
  {
    question: "Какво е антивирусна програма?",
    answer:
      "Антивирусната програма е софтуер, който предпазва вашето устройство от зловреден софтуер като вируси, троянци и червеи.",
  },
  {
    question: "Имам ли нужда от платен антивирус?",
    answer:
      "Платените антивирусни програми често предлагат по-добра защита, техническа поддръжка и допълнителни функции като VPN, родителски контрол и защита при онлайн банкиране.",
  },
  {
    question: "Какво е зловреден софтуер (malware)?",
    answer:
      "Зловреден софтуер е общ термин за всички програми, които могат да навредят на вашия компютър или да откраднат лична информация.",
  },
  {
    question: "Как да знам дали компютърът ми е заразен?",
    answer:
      "Някои признаци включват бавна работа на устройството, неочаквани прозорци, сривове или непознати програми, които се стартират автоматично.",
  },
  {
    question: "Как да избера правилния антивирус?",
    answer:
      "Изборът зависи от нуждите ви: добра защита, лекота на използване, цена и допълнителни инструменти. Прегледайте нашите ревюта за препоръки.",
  },
];

export default function FAQSection() {
  return (
    <section id="faq" className="bg-white py-16 px-4">
      <div className="max-w-5xl mx-auto text-center">
        <h2 className="text-3xl font-bold text-blue-800 mb-6">
          Frequently Asked Questions
        </h2>
        <p className="text-gray-600 mb-10">
          Common questions about antivirus software and computer security.
        </p>

        <div className="space-y-6 text-left">
          {faqs.map((faq, index) => (
            <div
              key={index}
              className="bg-gray-100 rounded-lg p-5 shadow-sm hover:shadow-md transition"
            >
              <h4 className="text-lg font-semibold text-blue-800 mb-2">
                {faq.question}
              </h4>
              <p className="text-gray-700 text-sm">{faq.answer}</p>
            </div>
          ))}
        </div>
      </div>
    </section>
  );
}
