import React, { useEffect, useState } from "react";
import "../index.css";

function AdminPage() {
  const [products, setProducts] = useState([]);
  const [form, setForm] = useState({ name: "", description: "", price: "", image: "" });
  const [fileName, setFileName] = useState("");
  const [editing, setEditing] = useState(null);

  useEffect(() => {
    fetchProducts();
  }, []);

  const fetchProducts = async () => {
    const res = await fetch("http://localhost:5000/api/admin");
    const data = await res.json();
    setProducts(data);
  };

  const convertToBase64 = (file) => {
    return new Promise((resolve, reject) => {
      const reader = new FileReader();
      reader.readAsDataURL(file);
      reader.onload = () => resolve(reader.result);
      reader.onerror = (error) => reject(error);
    });
  };

  const handleImageChange = async (e) => {
    const file = e.target.files[0];
    if (file) {
      const base64 = await convertToBase64(file);
      setForm({ ...form, image: base64 });
      setFileName(file.name);
    }
  };

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const method = editing ? "PUT" : "POST";
    const url = editing
      ? `http://localhost:5000/api/admin/${editing.ProductID}`
      : "http://localhost:5000/api/admin";

    const res = await fetch(url, {
      method,
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(form),
    });

    if (res.ok) {
      setForm({ name: "", description: "", price: "", image: "" });
      setFileName("");
      setEditing(null);
      fetchProducts();
    }
  };

  const handleEdit = (product) => {
    setForm({
      name: product.ProductName,
      description: product.Description,
      price: product.Price,
      image: product.ImageURL,
    });
    setEditing(product);
    setFileName("(existing image)");
  };

  const handleDelete = async (id) => {
    await fetch(`http://localhost:5000/api/admin/${id}`, { method: "DELETE" });
    fetchProducts();
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-purple-500 to-pink-500 text-white p-8">
      <div className="max-w-md mx-auto bg-white text-black rounded-xl shadow-lg p-6">
        <h2 className="text-xl font-bold mb-4 text-center">Add a New Product</h2>
        <form onSubmit={handleSubmit} className="space-y-4">
          <input
            type="text"
            name="name"
            placeholder="Name"
            value={form.name}
            onChange={handleChange}
            required
            className="w-full border px-3 py-2 rounded"
          />
          <input
            type="text"
            name="description"
            placeholder="Description"
            value={form.description}
            onChange={handleChange}
            required
            className="w-full border px-3 py-2 rounded"
          />
          <input
            type="text"
            name="price"
            placeholder="Price"
            value={form.price}
            onChange={handleChange}
            required
            className="w-full border px-3 py-2 rounded"
          />
          <input
            type="file"
            accept="image/*"
            onChange={handleImageChange}
            className="w-full"
          />
          {fileName && <p className="text-sm text-green-600">📷 {fileName}</p>}
          <button
            type="submit"
            className="w-full bg-blue-600 text-white py-2 rounded hover:bg-blue-700"
          >
            {editing ? "Update" : "Add"}
          </button>
        </form>
      </div>

      <div className="mt-10 max-w-4xl mx-auto bg-white rounded-xl shadow-lg overflow-x-auto">
        <table className="min-w-full text-sm text-gray-800">
          <thead>
            <tr className="bg-gray-200 text-left">
              <th className="py-2 px-4">Image</th>
              <th className="py-2 px-4">Name</th>
              <th className="py-2 px-4">Description</th>
              <th className="py-2 px-4">Price</th>
              <th className="py-2 px-4">Action</th>
            </tr>
          </thead>
          <tbody>
            {products.map((product) => (
              <tr key={product.ProductID} className="border-t">
                <td className="py-2 px-4">
                  <img
                    src={product.ImageURL}
                    alt={product.ProductName}
                    className="w-10 h-10 object-contain"
                  />
                </td>
                <td className="py-2 px-4 font-medium">{product.ProductName}</td>
                <td className="py-2 px-4">{product.Description}</td>
                <td className="py-2 px-4">{product.Price}</td>
                <td className="py-2 px-4 flex gap-2">
                  <button
                    onClick={() => handleEdit(product)}
                    className="bg-yellow-400 text-black px-3 py-1 rounded hover:bg-yellow-500"
                  >
                    Edit
                  </button>
                  <button
                    onClick={() => handleDelete(product.ProductID)}
                    className="bg-red-500 text-white px-3 py-1 rounded hover:bg-red-600"
                  >
                    Delete
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
}

export default AdminPage;
