import React, { useEffect, useState } from "react";
import { useParams, useNavigate } from "react-router-dom";
import ReviewSection from "./components/ReviewSection";

function ProductDetails() {
  const { id } = useParams();
  const [product, setProduct] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    fetch(`http://localhost:5000/api/products/${id}`)
      .then((res) => {
        if (!res.ok) throw new Error("Продуктът не е намерен");
        return res.json();
      })
      .then((data) => setProduct(data))
      .catch(() => navigate("/"));
  }, [id, navigate]);

  if (!product)
    return <div style={{ padding: "2rem", textAlign: "center" }}>Зареждане...</div>;

  return (
    <div className="product-details p-4 max-w-3xl mx-auto">
      <button
        onClick={() => navigate(-1)}
        className="mb-4 bg-gray-200 px-4 py-2 rounded hover:bg-gray-300"
      >
        ← Назад
      </button>
      <h2 className="text-2xl font-bold mb-2">{product.ProductName}</h2>
      <img src={product.ImageURL} alt={product.ProductName} className="h-40 mb-4 mx-auto" />
      <p className="mb-2">{product.Description}</p>
      <p className="mb-4"><strong>Цена:</strong> {product.Price}</p>
      <ReviewSection productId={product.ProductID} />
    </div>
  );
}

export default ProductDetails;
