import React, { useEffect, useState } from "react";

function ReviewSection({ productId }) {
  const [reviews, setReviews] = useState([]);
  const [reviewText, setReviewText] = useState("");
  const [rating, setRating] = useState(5);
  const [user, setUser] = useState(null);

  useEffect(() => {
    const token = localStorage.getItem("token");
    const userData = token ? JSON.parse(atob(token.split(".")[1])) : null;
    setUser(userData);

    fetch(`http://localhost:5000/api/reviews/${productId}`)
      .then((res) => res.json())
      .then((data) => setReviews(data));
  }, [productId]);

  const handleSubmit = async () => {
    if (!user) return;

    const token = localStorage.getItem("token");
    try {
      const response = await fetch("http://localhost:5000/api/reviews", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          Authorization: "Bearer " + token,
        },
        body: JSON.stringify({ productId, reviewText, rating })
      });

      if (response.ok) {
        const updated = await response.json();
        setReviews((prev) => [...prev, updated]);
        setReviewText("");
        setRating(5);
      }
    } catch (err) {
      console.error("‚ùå –ì—Ä–µ—à–∫–∞ –ø—Ä–∏ –∏–∑–ø—Ä–∞—â–∞–Ω–µ –Ω–∞ —Ä–µ–≤—é:", err);
    }
  };

  return (
    <div className="bg-white p-6 rounded-xl shadow">
      <h3 className="text-lg font-semibold text-center text-blue-700 mb-4">–†–µ–≤—é—Ç–∞</h3>

      {reviews.length === 0 ? (
        <p className="text-gray-500 text-center mb-4">–ù—è–º–∞ –Ω–∞–ª–∏—á–Ω–∏ —Ä–µ–≤—é—Ç–∞.</p>
      ) : (
        <div className="space-y-4 mb-6">
          {reviews.map((review, idx) => (
            <div key={idx} className="bg-gray-50 p-4 rounded shadow-sm">
              <div className="flex justify-between items-center mb-2">
                <span className="text-sm text-gray-600 font-medium">
                  üßë {review.UserName || "–ê–Ω–æ–Ω–∏–º–µ–Ω"}
                </span>
                <span className="text-yellow-500 font-semibold">
                  ‚≠ê {review.rating} / 5
                </span>
              </div>
              <p className="text-gray-700 text-sm">{review.reviewText}</p>
            </div>
          ))}
        </div>
      )}

      {user && (
        <div className="border-t pt-4">
          <h4 className="font-semibold mb-2">–î–æ–±–∞–≤–∏ —Ä–µ–≤—é</h4>
          <textarea
            value={reviewText}
            onChange={(e) => setReviewText(e.target.value)}
            placeholder="–ù–∞–ø–∏—à–∏ —Ä–µ–≤—é..."
            className="w-full border rounded p-2 mb-2"
          />
          <select
            value={rating}
            onChange={(e) => setRating(Number(e.target.value))}
            className="w-full border rounded p-2 mb-4"
          >
            {[5, 4, 3, 2, 1].map((r) => (
              <option key={r} value={r}>{r}</option>
            ))}
          </select>
          <button
            onClick={handleSubmit}
            className="bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700"
          >
            –ò–∑–ø—Ä–∞—Ç–∏ —Ä–µ–≤—é
          </button>
        </div>
      )}
    </div>
  );
}

export default ReviewSection;
