import React, { useEffect, useState } from "react";
import { useParams, useNavigate } from "react-router-dom";
import ReviewSection from "./components/ReviewSection";

function ProductDetails() {
  const { name } = useParams(); // –ò–∑–ø–æ–ª–∑–≤–∞–º–µ –ø–∞—Ä–∞–º–µ—Ç—ä—Ä–∞ –æ—Ç URL
  const [product, setProduct] = useState(null);
  const [features, setFeatures] = useState([]); 
  const navigate = useNavigate();

  // –ó–∞—Ä–µ–∂–¥–∞–Ω–µ –Ω–∞ –ø—Ä–æ–¥—É–∫—Ç–∞
  useEffect(() => {
    fetch(`http://localhost:5000/api/admin/name/${name}`)
      .then((res) => {
        if (!res.ok) throw new Error("–ü—Ä–æ–¥—É–∫—Ç—ä—Ç –Ω–µ –µ –Ω–∞–º–µ—Ä–µ–Ω");
        return res.json();
      })
      .then((data) => {
        console.log("üîç –î–∞–Ω–Ω–∏ –∑–∞ –ø—Ä–æ–¥—É–∫—Ç–∞:", data);
        setProduct(data);
      })
      .catch((err) => {
        console.error("‚ùå –ì—Ä–µ—à–∫–∞ –ø—Ä–∏ –∑–∞—Ä–µ–∂–¥–∞–Ω–µ:", err);
        navigate("/");
      });
  }, [name, navigate]);

  // –ó–∞—Ä–µ–∂–¥–∞–Ω–µ –Ω–∞ Key Features
  useEffect(() => {
    if (product?.ProductName) {
      fetch(`http://localhost:5000/api/products/${product.ProductName}/features`)
        .then(res => res.json())
        .then(setFeatures)
        .catch(err => console.error("‚ùå –ì—Ä–µ—à–∫–∞ –ø—Ä–∏ –∑–∞—Ä–µ–∂–¥–∞–Ω–µ –Ω–∞ features:", err));
    }
  }, [product?.ProductName]);

  if (!product) {
    return <div style={{ padding: "2rem", textAlign: "center" }}>–ó–∞—Ä–µ–∂–¥–∞–Ω–µ...</div>;
  }

  return (
    <div className="product-details" style={{ padding: "2rem" }}>
      <button onClick={() => navigate(-1)}>‚Üê –ù–∞–∑–∞–¥</button>
      <h2>{product.ProductName}</h2>
      <img src={product.ImageURL} alt={product.ProductName} style={{ height: "120px" }} />
      <p>{product.Description}</p>
      <p><strong>–¶–µ–Ω–∞:</strong> {product.Price}</p>

      {/* üîë Key Features */}
      <h3 style={{ marginTop: "1rem" }}>–û—Å–Ω–æ–≤–Ω–∏ —Ñ—É–Ω–∫—Ü–∏–∏:</h3>
      <ul>
        {features.map((f, i) => (
          <li key={i}>‚úîÔ∏è {f}</li>
        ))}
      </ul>

      {/* üìù –†–µ–≤—é —Å–µ–∫—Ü–∏—è */}
      <ReviewSection productId={product.ProductID} />
    </div>
  );
}

export default ProductDetails;
