import React, { useEffect, useState } from "react";

export default function TopAntivirusSection() {
  const [products, setProducts] = useState([]);
  const [sortBy, setSortBy] = useState("");

  useEffect(() => {
    fetch("http://localhost:5000/api/admin/products")
      .then((res) => res.json())
      .then((data) => setProducts(Array.isArray(data) ? data : []))
      .catch((err) => console.error("❌ Грешка при зареждане:", err));
  }, []);

  const sortedProducts = [...products];
  if (sortBy === "price") {
    sortedProducts.sort((a, b) => parseFloat(a.Price) - parseFloat(b.Price));
  } else if (sortBy === "rating") {
    sortedProducts.sort((a, b) => (b.AverageRating || 0) - (a.AverageRating || 0));
  }

  return (
    <section id="top-picks" className="bg-white py-16">
      <div className="max-w-6xl mx-auto px-4 text-center">
        <h2 className="text-2xl font-bold text-blue-800 mb-2">Top Antivirus Picks</h2>
        <p className="text-gray-600 text-sm mb-10">
          Our recommended antivirus software based on performance, protection, and value.
        </p>

        <div className="mb-6 flex justify-center gap-4">
          <button
            onClick={() => setSortBy("rating")}
            className="bg-gray-200 hover:bg-blue-200 px-4 py-1 rounded"
          >
            Sort by Rating
          </button>
          <button
            onClick={() => setSortBy("price")}
            className="bg-gray-200 hover:bg-blue-200 px-4 py-1 rounded"
          >
            Sort by Price
          </button>
        </div>

        <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 gap-6">
          {sortedProducts.map((product, index) => (
            <div
              key={product.ProductID}
              className="bg-gray-100 rounded-lg shadow p-6 relative"
            >
              {index === 0 && (
                <span className="absolute top-0 left-0 bg-blue-600 text-white text-xs px-2 py-1 rounded-br-md">
                  🥇 Editor's Choice
                </span>
              )}
              <img
                src={product.ImageURL}
                alt={product.ProductName}
                className="mx-auto mb-4 h-16"
              />
              <h3 className="text-lg font-semibold">{product.ProductName}</h3>
              <p className="text-sm text-gray-500 mb-1">{product.Description}</p>
              <p className="text-blue-700 font-bold mt-1">{product.Price} лв</p>
              {product.AverageRating ? (
                <p className="text-yellow-600 text-sm mt-1">
                  ⭐ {product.AverageRating.toFixed(1)} / 5
                </p>
              ) : (
                <p className="italic text-gray-500 text-sm mt-1">Няма оценка</p>
              )}
              <div className="mt-4 flex justify-center gap-2">
                <a
                  href={product.OfficialWebsite}
                  target="_blank"
                  rel="noopener noreferrer"
                  className="bg-blue-600 text-white px-3 py-1 rounded hover:bg-blue-700 text-sm"
                >
                  Visit Site
                </a>
                <a
                  href={`/product/${product.ProductID}`}
                  className="bg-gray-300 px-3 py-1 rounded hover:bg-gray-400 text-sm"
                >
                  Прочети Ревю
                </a>
              </div>
              {product.KeyFeatures && (
                <div className="mt-4 text-left text-sm">
                  <h4 className="font-semibold text-black">Key Features</h4>
                  <ul className="list-disc list-inside text-gray-700">
                    {product.KeyFeatures.split("\n").map((feature, i) => (
                      <li key={i}>{feature}</li>
                    ))}
                  </ul>
                </div>
              )}
            </div>
          ))}
        </div>
      </div>
    </section>
  );
}
