import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import ExpertReviews from "./ExpertReviews";

function AntivirusListT() {
  const [products, setProducts] = useState([]);
  const [sortBy, setSortBy] = useState("");
  const navigate = useNavigate();

  useEffect(() => {
    fetch("http://localhost:5000/api/admin")
      .then((res) => res.json())
      .then(async (data) => {
        const enriched = await Promise.all(
          data.map(async (product) => {
            try {
              const res = await fetch(
                `http://localhost:5000/api/products/${product.ProductName}/features`
              );
              const features = await res.json();
              return { ...product, features };
            } catch {
              return { ...product, features: [] };
            }
          })
        );
        setProducts(enriched);
      })
      .catch((err) => console.error("❌ Error loading products:", err));
  }, []);

  const sortedProducts = [...products];
  if (sortBy === "price") {
    sortedProducts.sort((a, b) => parseFloat(a.Price) - parseFloat(b.Price));
  } else if (sortBy === "rating") {
    sortedProducts.sort((a, b) => (b.AverageRating || 0) - (a.AverageRating || 0));
  }

  const topRated = [...products]
    .sort((a, b) => (b.AverageRating || 0) - (a.AverageRating || 0))[0]?.ProductID;

  const bestValue = [...products]
    .sort((a, b) => parseFloat(a.Price) - parseFloat(b.Price))[0]?.ProductID;

  return (
    <section id="top-picks" className="bg-gray-50 py-16 px-4">
      <div className="max-w-7xl mx-auto">
        <div className="text-center mb-10">
          <div className="flex justify-center gap-4 mb-4">
            <button
              onClick={() => setSortBy("rating")}
              className={`px-4 py-2 rounded font-medium text-sm ${
                sortBy === "rating" ? "bg-blue-600 text-white" : "bg-gray-200"
              }`}
            >
              Sort by Rating
            </button>
            <button
              onClick={() => setSortBy("price")}
              className={`px-4 py-2 rounded font-medium text-sm ${
                sortBy === "price" ? "bg-blue-600 text-white" : "bg-gray-200"
              }`}
            >
              Sort by Price
            </button>
          </div>
          <h2 className="text-3xl font-bold text-gray-800">Our Top Antivirus Picks</h2>
          <p className="text-gray-600 mt-2 max-w-xl mx-auto">
            Based on real-world testing, performance, usability, and features.
          </p>
        </div>

        <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 gap-8">
          {sortedProducts.map((product) => (
            <div
              key={product.ProductID}
              className="relative bg-white rounded-xl shadow-md p-6 text-center hover:shadow-xl transition-transform hover:scale-105"
            >
              {product.ProductID === topRated && (
                <div className="absolute top-0 left-0 w-full bg-blue-600 text-white py-1 rounded-t-xl text-sm font-semibold">
                  🏅 Editor's Choice
                </div>
              )}
              {product.ProductID === bestValue && product.ProductID !== topRated && (
                <div className="absolute top-0 left-0 w-full bg-green-600 text-white py-1 rounded-t-xl text-sm font-semibold">
                  💰 Best Value
                </div>
              )}

              <img
                src={
                  product.ImageURL?.startsWith("data:image")
                    ? product.ImageURL
                    : product.ImageURL?.startsWith("http")
                    ? product.ImageURL
                    : `http://localhost:5000${product.ImageURL}`
                }
                alt={product.ProductName}
                className="h-20 mx-auto my-6 object-contain"
              />

              <h3 className="font-semibold text-lg text-gray-800">
                {product.ProductName}
              </h3>
              <p className="text-sm text-gray-600 mt-1">
                {product.Description}
              </p>
              <p className="text-blue-700 font-bold mt-2">{product.Price}</p>

              {product.features?.length > 0 && (
                <ul className="text-left mt-4 space-y-1 text-sm">
                  <h4 className="font-medium text-gray-700 mb-1">Key Features:</h4>
                  {product.features.slice(0, 4).map((feature, i) => (
                    <li key={i} className="flex items-start text-gray-600">
                      <span className="mr-2 text-green-600">✔</span>
                      <span>{feature.Feature || feature}</span>
                    </li>
                  ))}
                </ul>
              )}

              {typeof product.AverageRating === "number" ? (
                <p className="text-yellow-500 mt-2 font-medium">
                  ⭐ {product.AverageRating.toFixed(1)} / 5.0
                </p>
              ) : (
                <p className="text-gray-400 mt-2 italic">No rating yet</p>
              )}

              <div className="flex justify-center gap-2 mt-4">
                <button
                  className="bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700 text-sm"
                  onClick={() => window.open("https://www.example.com", "_blank")}
                >
                  Visit Site
                </button>
                <button
                  className="bg-gray-200 text-blue-700 px-4 py-2 rounded hover:bg-gray-300 text-sm"
                  onClick={() =>
                    navigate(`/product/${encodeURIComponent(product.ProductName)}`)
                  }
                >
                  Read Review
                </button>
              </div>
            </div>
          ))}
        </div>

        <div className="mt-16">
          <ExpertReviews />
        </div>
      </div>
    </section>
  );
}

export default AntivirusListT;
