// ✅ Admin Panel с добавени Expert Review полета под таблицата с продукти
import React, { useEffect, useState } from "react";
import Navbar from "./components/Navbar";
import "./index.css";

function AdminPageT() {
  const [products, setProducts] = useState([]);
  const [reviews, setReviews] = useState([]);
  const [name, setName] = useState("");
  const [description, setDescription] = useState("");
  const [price, setPrice] = useState("");
  const [image, setImage] = useState("");
  const [fileName, setFileName] = useState("");
  const [features, setFeatures] = useState("");
  const [editingId, setEditingId] = useState(null);
  const [selectedProductId, setSelectedProductId] = useState("");
  const [expertReview, setExpertReview] = useState({
    ourReview: "",
    pros: "",
    cons: "",
    bottomLine: "",
    malware: "",
    performance: "",
    ui: "",
    value: "",
    protectionFeatures: ""
  });

  useEffect(() => {
    fetchProducts();
    fetchReviews();
  }, []);

  const fetchProducts = async () => {
    try {
      const res = await fetch("http://localhost:5000/api/admin");
      const data = await res.json();
      setProducts(Array.isArray(data) ? data : []);
    } catch (err) {
      console.error("❌ Грешка при зареждане:", err);
    }
  };

  const fetchReviews = async () => {
    try {
      const res = await fetch("http://localhost:5000/api/expert");
      const data = await res.json();
      setReviews(Array.isArray(data) ? data : []);
    } catch (err) {
      console.error("❌ Грешка при ревюта:", err);
    }
  };

  const convertToBase64 = (file) => new Promise((resolve, reject) => {
    const reader = new FileReader();
    reader.readAsDataURL(file);
    reader.onload = () => resolve(reader.result);
    reader.onerror = (error) => reject(error);
  });

  const handleImageChange = async (e) => {
    const file = e.target.files[0];
    if (file) {
      const base64 = await convertToBase64(file);
      setImage(base64);
      setFileName(file.name);
    }
  };

  const handleAdd = async () => {
    if (!name || !description || !price || !image) {
      alert("Моля, попълнете всички полета.");
      return;
    }
    const method = editingId ? "PUT" : "POST";
    const url = editingId
      ? `http://localhost:5000/api/admin/${editingId}`
      : "http://localhost:5000/api/admin";

    try {
      const res = await fetch(url, {
        method,
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ name, description, image, price })
      });
      const data = await res.json();
      if (res.ok) {
        alert(data.message);
        setName(""); setDescription(""); setPrice(""); setImage("");
        setFileName(""); setFeatures(""); setEditingId(null);
        fetchProducts();
      } else alert("Грешка при заявката");
    } catch (err) {
      console.error("❌ Грешка при заявката:", err);
    }
  };

  const handleDelete = async (id) => {
    try {
      const res = await fetch(`http://localhost:5000/api/admin/${id}`, { method: "DELETE" });
      const data = await res.json();
      alert(data.message);
      fetchProducts();
    } catch (err) {
      console.error("❌ Грешка при изтриване:", err);
    }
  };

  const handleFeatureSave = async () => {
    if (!editingId) return;
    try {
      const res = await fetch(`http://localhost:5000/api/admin/${editingId}/features`, {
        method: "PUT",
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer ${localStorage.getItem("token")}`,
        },
        body: JSON.stringify({ features })
      });
      const data = await res.json();
      alert(data.message);
      setFeatures(""); setEditingId(null);
      fetchProducts();
    } catch (err) {
      console.error("❌ Грешка при записване на features:", err);
    }
  };

  const handleSaveExpertReview = async () => {
    if (!selectedProductId) return alert("Избери продукт за ревю");
    try {
      const res = await fetch(`http://localhost:5000/api/expert/${selectedProductId}`, {
        method: "PUT",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(expertReview)
      });
      const data = await res.json();
      alert(data.message || "Успешно записано!");
      fetchReviews();
    } catch (err) {
      console.error("❌ Грешка при запис на ревю:", err);
    }
  };

  return (
    <div className="admin-page">
      <Navbar />

      {/* Форма за добавяне на продукт */}
      <div className="admin-form">
        <h2>{editingId ? "Редакция на продукт" : "Добавяне на продукт"}</h2>
        <input type="text" placeholder="Име" value={name} onChange={(e) => setName(e.target.value)} />
        <input type="text" placeholder="Описание" value={description} onChange={(e) => setDescription(e.target.value)} />
        <input type="text" placeholder="Цена" value={price} onChange={(e) => setPrice(e.target.value)} />
        <label htmlFor="fileUpload" className="custom-file-upload">Изображение</label>
        <input id="fileUpload" type="file" accept="image/*" onChange={handleImageChange} style={{ display: "none" }} />
        {fileName && <p>Избран файл: {fileName}</p>}
        <button onClick={handleAdd}>{editingId ? "Запази" : "Добави"}</button>
      </div>

      {editingId && (
        <div className="features-section">
          <h3>Редакция на Key Features</h3>
          <textarea
            className="features-textarea"
            value={features}
            onChange={(e) => setFeatures(e.target.value)}
            placeholder="Въведи Key Features по редове..."
          />
          <button onClick={handleFeatureSave}>💾 Запиши Key Features</button>
        </div>
      )}

      {/* Таблица с продукти */}
      <div className="product-table">
        <table>
          <thead>
            <tr>
              <th>Снимка</th>
              <th>Име</th>
              <th>Описание</th>
              <th>Цена</th>
              <th>Действие</th>
            </tr>
          </thead>
          <tbody>
            {products.map((p) => (
              <tr key={p.ProductID}>
                <td><img src={p.ImageURL} alt={p.ProductName} style={{ width: 50 }} /></td>
                <td>{p.ProductName}</td>
                <td>{p.Description}</td>
                <td>{p.Price}</td>
                <td style={{ display: "flex", flexDirection: "column", gap: "0.5rem" }}>
                  <button className="edit-button" onClick={() => {
                    setEditingId(p.ProductID);
                    setName(p.ProductName);
                    setDescription(p.Description);
                    setPrice(p.Price);
                    setImage(p.ImageURL);
                    setFeatures(p.KeyFeatures || "");
                  }}>Редактирай</button>
                  <button className="delete-button" onClick={() => handleDelete(p.ProductID)}>Изтрий</button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>

      {/* Expert Review секция */}
      <div className="expert-review-section">
        <h3>Expert Review за Продукта</h3>
        <select value={selectedProductId} onChange={(e) => setSelectedProductId(e.target.value)}>
          <option value="">-- Избери продукт --</option>
          {products.map(p => (
            <option key={p.ProductID} value={p.ProductID}>{p.ProductName}</option>
          ))}
        </select>
        <textarea placeholder="Our Review" value={expertReview.ourReview} onChange={(e) => setExpertReview({ ...expertReview, ourReview: e.target.value })} />
        <textarea placeholder="Pros (+ по редове)" value={expertReview.pros} onChange={(e) => setExpertReview({ ...expertReview, pros: e.target.value })} />
        <textarea placeholder="Cons (- по редове)" value={expertReview.cons} onChange={(e) => setExpertReview({ ...expertReview, cons: e.target.value })} />
        <textarea placeholder="Bottom Line" value={expertReview.bottomLine} onChange={(e) => setExpertReview({ ...expertReview, bottomLine: e.target.value })} />

        <input placeholder="Malware Protection (напр. 4.9)" value={expertReview.malware} onChange={(e) => setExpertReview({ ...expertReview, malware: e.target.value })} />
        <input placeholder="Performance Impact (напр. 2.9)" value={expertReview.performance} onChange={(e) => setExpertReview({ ...expertReview, performance: e.target.value })} />
        <input placeholder="User Interface (напр. 4.7)" value={expertReview.ui} onChange={(e) => setExpertReview({ ...expertReview, ui: e.target.value })} />
        <input placeholder="Value for Money (напр. 4.6)" value={expertReview.value} onChange={(e) => setExpertReview({ ...expertReview, value: e.target.value })} />

        <textarea placeholder="Protection Features (по редове)" value={expertReview.protectionFeatures} onChange={(e) => setExpertReview({ ...expertReview, protectionFeatures: e.target.value })} />

        <button onClick={handleSaveExpertReview}>💾 Запиши Expert Review</button>
      </div>
    </div>
  );
}

export default AdminPageT;
