// ‚úÖ Admin Panel —Å –¥–æ–±–∞–≤–µ–Ω–∏ Expert Review –ø–æ–ª–µ—Ç–∞ –ø–æ–¥ —Ç–∞–±–ª–∏—Ü–∞—Ç–∞ —Å –ø—Ä–æ–¥—É–∫—Ç–∏
import React, { useEffect, useState } from "react";
import Navbar from "./components/Navbar";
import "./index.css";

function AdminPageT() {
  const [products, setProducts] = useState([]);
  const [reviews, setReviews] = useState([]);
  const [name, setName] = useState("");
  const [description, setDescription] = useState("");
  const [price, setPrice] = useState("");
  const [image, setImage] = useState("");
  const [fileName, setFileName] = useState("");
  const [features, setFeatures] = useState("");
  const [editingId, setEditingId] = useState(null);
  const [selectedProductId, setSelectedProductId] = useState("");
  const [expertReview, setExpertReview] = useState({
    ourReview: "",
    pros: "",
    cons: "",
    bottomLine: "",
    malware: "",
    performance: "",
    ui: "",
    value: "",
    protectionFeatures: ""
  });

  useEffect(() => {
    fetchProducts();
    fetchReviews();
  }, []);

  const fetchProducts = async () => {
    try {
      const res = await fetch("http://localhost:5000/api/admin");
      const data = await res.json();
      setProducts(Array.isArray(data) ? data : []);
    } catch (err) {
      console.error("‚ùå –ì—Ä–µ—à–∫–∞ –ø—Ä–∏ –∑–∞—Ä–µ–∂–¥–∞–Ω–µ:", err);
    }
  };

  const fetchReviews = async () => {
    try {
      const res = await fetch("http://localhost:5000/api/expert");
      const data = await res.json();
      setReviews(Array.isArray(data) ? data : []);
    } catch (err) {
      console.error("‚ùå –ì—Ä–µ—à–∫–∞ –ø—Ä–∏ —Ä–µ–≤—é—Ç–∞:", err);
    }
  };

  const convertToBase64 = (file) => new Promise((resolve, reject) => {
    const reader = new FileReader();
    reader.readAsDataURL(file);
    reader.onload = () => resolve(reader.result);
    reader.onerror = (error) => reject(error);
  });

  const handleImageChange = async (e) => {
    const file = e.target.files[0];
    if (file) {
      const base64 = await convertToBase64(file);
      setImage(base64);
      setFileName(file.name);
    }
  };

  const handleAdd = async () => {
    if (!name || !description || !price || !image) {
      alert("–ú–æ–ª—è, –ø–æ–ø—ä–ª–Ω–µ—Ç–µ –≤—Å–∏—á–∫–∏ –ø–æ–ª–µ—Ç–∞.");
      return;
    }
    const method = editingId ? "PUT" : "POST";
    const url = editingId
      ? `http://localhost:5000/api/admin/${editingId}`
      : "http://localhost:5000/api/admin";

    try {
      const res = await fetch(url, {
        method,
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ name, description, image, price })
      });
      const data = await res.json();
      if (res.ok) {
        alert(data.message);
        setName(""); setDescription(""); setPrice(""); setImage("");
        setFileName(""); setFeatures(""); setEditingId(null);
        fetchProducts();
      } else alert("–ì—Ä–µ—à–∫–∞ –ø—Ä–∏ –∑–∞—è–≤–∫–∞—Ç–∞");
    } catch (err) {
      console.error("‚ùå –ì—Ä–µ—à–∫–∞ –ø—Ä–∏ –∑–∞—è–≤–∫–∞—Ç–∞:", err);
    }
  };

  const handleDelete = async (id) => {
    try {
      const res = await fetch(`http://localhost:5000/api/admin/${id}`, { method: "DELETE" });
      const data = await res.json();
      alert(data.message);
      fetchProducts();
    } catch (err) {
      console.error("‚ùå –ì—Ä–µ—à–∫–∞ –ø—Ä–∏ –∏–∑—Ç—Ä–∏–≤–∞–Ω–µ:", err);
    }
  };

  const handleFeatureSave = async () => {
    if (!editingId) return;
    try {
      const res = await fetch(`http://localhost:5000/api/admin/${editingId}/features`, {
        method: "PUT",
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer ${localStorage.getItem("token")}`,
        },
        body: JSON.stringify({ features })
      });
      const data = await res.json();
      alert(data.message);
      setFeatures(""); setEditingId(null);
      fetchProducts();
    } catch (err) {
      console.error("‚ùå –ì—Ä–µ—à–∫–∞ –ø—Ä–∏ –∑–∞–ø–∏—Å–≤–∞–Ω–µ –Ω–∞ features:", err);
    }
  };

  const handleSaveExpertReview = async () => {
    if (!selectedProductId) return alert("–ò–∑–±–µ—Ä–∏ –ø—Ä–æ–¥—É–∫—Ç –∑–∞ —Ä–µ–≤—é");
    try {
      const res = await fetch(`http://localhost:5000/api/expert/${selectedProductId}`, {
        method: "PUT",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(expertReview)
      });
      const data = await res.json();
      alert(data.message || "–£—Å–ø–µ—à–Ω–æ –∑–∞–ø–∏—Å–∞–Ω–æ!");
      fetchReviews();
    } catch (err) {
      console.error("‚ùå –ì—Ä–µ—à–∫–∞ –ø—Ä–∏ –∑–∞–ø–∏—Å –Ω–∞ —Ä–µ–≤—é:", err);
    }
  };

  return (
    <div className="admin-page">
      <Navbar />

      {/* –§–æ—Ä–º–∞ –∑–∞ –¥–æ–±–∞–≤—è–Ω–µ –Ω–∞ –ø—Ä–æ–¥—É–∫—Ç */}
      <div className="admin-form">
        <h2>{editingId ? "–†–µ–¥–∞–∫—Ü–∏—è –Ω–∞ –ø—Ä–æ–¥—É–∫—Ç" : "–î–æ–±–∞–≤—è–Ω–µ –Ω–∞ –ø—Ä–æ–¥—É–∫—Ç"}</h2>
        <input type="text" placeholder="–ò–º–µ" value={name} onChange={(e) => setName(e.target.value)} />
        <input type="text" placeholder="–û–ø–∏—Å–∞–Ω–∏–µ" value={description} onChange={(e) => setDescription(e.target.value)} />
        <input type="text" placeholder="–¶–µ–Ω–∞" value={price} onChange={(e) => setPrice(e.target.value)} />
        <label htmlFor="fileUpload" className="custom-file-upload">–ò–∑–æ–±—Ä–∞–∂–µ–Ω–∏–µ</label>
        <input id="fileUpload" type="file" accept="image/*" onChange={handleImageChange} style={{ display: "none" }} />
        {fileName && <p>–ò–∑–±—Ä–∞–Ω —Ñ–∞–π–ª: {fileName}</p>}
        <button onClick={handleAdd}>{editingId ? "–ó–∞–ø–∞–∑–∏" : "–î–æ–±–∞–≤–∏"}</button>
      </div>

      {editingId && (
        <div className="features-section">
          <h3>–†–µ–¥–∞–∫—Ü–∏—è –Ω–∞ Key Features</h3>
          <textarea
            className="features-textarea"
            value={features}
            onChange={(e) => setFeatures(e.target.value)}
            placeholder="–í—ä–≤–µ–¥–∏ Key Features –ø–æ —Ä–µ–¥–æ–≤–µ..."
          />
          <button onClick={handleFeatureSave}>üíæ –ó–∞–ø–∏—à–∏ Key Features</button>
        </div>
      )}

      {/* –¢–∞–±–ª–∏—Ü–∞ —Å –ø—Ä–æ–¥—É–∫—Ç–∏ */}
      <div className="product-table">
        <table>
          <thead>
            <tr>
              <th>–°–Ω–∏–º–∫–∞</th>
              <th>–ò–º–µ</th>
              <th>–û–ø–∏—Å–∞–Ω–∏–µ</th>
              <th>–¶–µ–Ω–∞</th>
              <th>–î–µ–π—Å—Ç–≤–∏–µ</th>
            </tr>
          </thead>
          <tbody>
            {products.map((p) => (
              <tr key={p.ProductID}>
                <td><img src={p.ImageURL} alt={p.ProductName} style={{ width: 50 }} /></td>
                <td>{p.ProductName}</td>
                <td>{p.Description}</td>
                <td>{p.Price}</td>
                <td style={{ display: "flex", flexDirection: "column", gap: "0.5rem" }}>
                  <button className="edit-button" onClick={() => {
                    setEditingId(p.ProductID);
                    setName(p.ProductName);
                    setDescription(p.Description);
                    setPrice(p.Price);
                    setImage(p.ImageURL);
                    setFeatures(p.KeyFeatures || "");
                  }}>–†–µ–¥–∞–∫—Ç–∏—Ä–∞–π</button>
                  <button className="delete-button" onClick={() => handleDelete(p.ProductID)}>–ò–∑—Ç—Ä–∏–π</button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>

      {/* Expert Review —Å–µ–∫—Ü–∏—è */}
      <div className="expert-review-section">
        <h3>Expert Review –∑–∞ –ü—Ä–æ–¥—É–∫—Ç–∞</h3>
        <select value={selectedProductId} onChange={(e) => setSelectedProductId(e.target.value)}>
          <option value="">-- –ò–∑–±–µ—Ä–∏ –ø—Ä–æ–¥—É–∫—Ç --</option>
          {products.map(p => (
            <option key={p.ProductID} value={p.ProductID}>{p.ProductName}</option>
          ))}
        </select>
        <textarea placeholder="Our Review" value={expertReview.ourReview} onChange={(e) => setExpertReview({ ...expertReview, ourReview: e.target.value })} />
        <textarea placeholder="Pros (+ –ø–æ —Ä–µ–¥–æ–≤–µ)" value={expertReview.pros} onChange={(e) => setExpertReview({ ...expertReview, pros: e.target.value })} />
        <textarea placeholder="Cons (- –ø–æ —Ä–µ–¥–æ–≤–µ)" value={expertReview.cons} onChange={(e) => setExpertReview({ ...expertReview, cons: e.target.value })} />
        <textarea placeholder="Bottom Line" value={expertReview.bottomLine} onChange={(e) => setExpertReview({ ...expertReview, bottomLine: e.target.value })} />

        <input placeholder="Malware Protection (–Ω–∞–ø—Ä. 4.9)" value={expertReview.malware} onChange={(e) => setExpertReview({ ...expertReview, malware: e.target.value })} />
        <input placeholder="Performance Impact (–Ω–∞–ø—Ä. 2.9)" value={expertReview.performance} onChange={(e) => setExpertReview({ ...expertReview, performance: e.target.value })} />
        <input placeholder="User Interface (–Ω–∞–ø—Ä. 4.7)" value={expertReview.ui} onChange={(e) => setExpertReview({ ...expertReview, ui: e.target.value })} />
        <input placeholder="Value for Money (–Ω–∞–ø—Ä. 4.6)" value={expertReview.value} onChange={(e) => setExpertReview({ ...expertReview, value: e.target.value })} />

        <textarea placeholder="Protection Features (–ø–æ —Ä–µ–¥–æ–≤–µ)" value={expertReview.protectionFeatures} onChange={(e) => setExpertReview({ ...expertReview, protectionFeatures: e.target.value })} />

        <button onClick={handleSaveExpertReview}>üíæ –ó–∞–ø–∏—à–∏ Expert Review</button>
      </div>
    </div>
  );
}

export default AdminPageT;
