import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";

function AntivirusListT() {
  const [products, setProducts] = useState([]);
  const [sortBy, setSortBy] = useState("");
  const navigate = useNavigate();
  const [showComparison, setShowComparison] = useState(false);

  useEffect(() => {
    fetch("http://localhost:5000/api/admin")
      .then((res) => res.json())
      .then((data) => {
        console.log("🔄 Заредени продукти: ", data);
        setProducts(Array.isArray(data) ? data : []);
      })
      .catch((err) => console.error("❌ Грешка при зареждане:", err));
  }, []);

  const sortedProducts = [...products];
  if (sortBy === "price") {
    sortedProducts.sort((a, b) => parseFloat(a.Price) - parseFloat(b.Price));
  } else if (sortBy === "rating") {
    sortedProducts.sort((a, b) => (b.AverageRating || 0) - (a.AverageRating || 0));
  }

  const topRated = [...products]
    .sort((a, b) => (b.AverageRating || 0) - (a.AverageRating || 0))[0]?.ProductID;

  const bestValue = [...products]
    .sort((a, b) => parseFloat(a.Price) - parseFloat(b.Price))[0]?.ProductID;

  return (
    <section className="p-8 bg-gray-100 min-h-screen">
      {/* Заглавие центрирано, филтри отляво */}
      <div className="relative mb-6 px-4">
        <div className="absolute left-0 top-0 flex gap-2 text-sm font-medium">
          <button
            onClick={() => setSortBy("rating")}
            className={`px-3 py-1 rounded ${
              sortBy === "rating" ? "bg-blue-600 text-white" : "bg-gray-200"
            }`}
          >
            Sort by Rating
          </button>
          <button
            onClick={() => setSortBy("price")}
            className={`px-3 py-1 rounded ${
              sortBy === "price" ? "bg-blue-600 text-white" : "bg-gray-200"
            }`}
          >
            Sort by Price
          </button>
        </div>
        <h2 className="text-2xl font-bold text-center">
            Our Top Antivirus Picks
        </h2>
        <p className="text-gray-600 mt-2 text-center" >
        Based on comprehensive testing and evaluation, these are our recommended antivirus <br/> solutions for complete protection.
       </p>

      </div>

      {/* Карти */}
      <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
        {sortedProducts.map((product) => (
          <div
            key={product.ProductID}
            className="relative bg-white p-4 rounded-lg shadow-md text-center"
          >
            {product.ProductID === topRated && (
              <div className="absolute top-0 left-0 w-full bg-blue-600 text-white py-1 rounded-t-lg text-sm font-semibold">
                🏅 Editor's Choice
              </div>
            )}
            {product.ProductID === bestValue && product.ProductID !== topRated && (
              <div className="absolute top-0 left-0 w-full bg-green-600 text-white py-1 rounded-t-lg text-sm font-semibold">
                💰 Best Value
              </div>
            )}

            <img
              src={product.ImageURL}
              alt={product.ProductName}
              className="h-20 mx-auto my-6"
            />
            <h3 className="font-semibold">{product.ProductName}</h3>
            <p className="text-sm text-gray-600 mb-2">{product.Description}</p>
            <p className="text-blue-700 font-bold">{product.Price}</p>

            {typeof product.AverageRating === "number" && !isNaN(product.AverageRating) ? (
              <p className="text-yellow-500 mt-1 font-medium">
                ⭐ {product.AverageRating.toFixed(1)} / 5.0
              </p>
            ) : (
              <p className="text-gray-400 mt-1 italic">Няма оценка</p>
            )}

            <div className="flex justify-center gap-2 mt-4">
              <button
                className="bg-blue-600 text-white px-3 py-1 rounded hover:bg-blue-700"
                onClick={() => window.open("https://www.example.com", "_blank")}
              >
                Visit Site
              </button>
              <button
                className="bg-gray-200 text-blue-700 px-3 py-1 rounded hover:bg-gray-300"
                onClick={() =>
                  navigate(`/product/${encodeURIComponent(product.ProductName)}`)
                }
              >
                Прочети Ревю
              </button>
            </div>
            <div className="flex justify-center mt-10">
            <button
              onClick={() => setShowComparison(!showComparison)}
              className="px-4 py-2 font-semibold bg-white text-black shadow rounded"
            >
              Detailed Feature Comparison {showComparison ? "▲" : "▼"}
            </button>
            {showComparison && (
  <div className="mt-6 bg-white p-4 rounded shadow overflow-x-auto">
    <table className="w-full text-left border-collapse">
      <thead>
        <tr className="bg-gray-200">
          <th className="p-2">Antivirus</th>
          <th className="p-2">Real-time Protection</th>
          <th className="p-2">VPN</th>
          <th className="p-2">Password Manager</th>
          <th className="p-2">Parental Controls</th>
          <th className="p-2">Firewall</th>
        </tr>
      </thead>
      <tbody>
        <tr className="border-t">
          <td className="p-2">Avast</td>
          <td className="p-2">✅</td>
          <td className="p-2">✅</td>
          <td className="p-2">✅</td>
          <td className="p-2">✅</td>
          <td className="p-2">✅</td>
        </tr>
        <tr className="border-t">
          <td className="p-2">McAfee</td>
          <td className="p-2">✅</td>
          <td className="p-2">✅</td>
          <td className="p-2">❌</td>
          <td className="p-2">✅</td>
          <td className="p-2">✅</td>
        </tr>
        {/* Добави още редове при нужда */}
      </tbody>
    </table>
  </div>
)}

          </div>
          </div>         
        ))}
      </div>
    </section>
  );
}

export default AntivirusListT;
