import React, { useEffect, useState } from "react";
import { Link, useNavigate } from "react-router-dom";

export default function Navbar() {
  const [isAdmin, setIsAdmin] = useState(false);
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const navigate = useNavigate();

  useEffect(() => {
    const adminStatus = localStorage.getItem("isAdmin");
    const token = localStorage.getItem("token");

    setIsAdmin(adminStatus === "true");
    setIsLoggedIn(!!token); // ако има token -> true
  }, []);

  const handleLogout = () => {
    localStorage.removeItem("token");
    localStorage.removeItem("isAdmin");
    navigate("/");
    window.location.reload(); // презареждане за да се опресни менюто
  };

  return (
    <nav className="w-full bg-white shadow-md fixed top-0 left-0 z-50">
      <div className="max-w-7xl mx-auto px-4 py-3 flex items-center justify-between">
        <div className="text-xl font-extrabold text-gray-800">
          Antivirus Reviews
        </div>
        <div className="space-x-4 flex items-center">
          <a
            href="#top-picks"
            className="text-gray-600 hover:text-blue-600 font-medium"
          >
            Top Picks
          </a>
          <a
            href="#expert-reviews"
            className="text-gray-600 hover:text-blue-600 font-medium"
          >
            Expert Reviews
          </a>
          <a
            href="#faq"
            className="text-gray-600 hover:text-blue-600 font-medium"
          >
            FAQ
          </a>

          {isAdmin && (
            <Link
              to="/admin"
              className="px-4 py-2 border  text-green-600 rounded hover:bg-green-600 hover:text-white transition font-medium"
            >
              Админ Панел
            </Link>
          )}

          {isLoggedIn ? (
            <button
              onClick={handleLogout}
              className="px-4 py-2 border border-red-600 text-red-600 rounded hover:bg-red-600 hover:text-white transition font-medium"
            >
              Изход
            </button>
          ) : (
            <Link
              to="/login"
              className="px-4 py-2 border border-blue-600 text-blue-600 rounded hover:bg-blue-600 hover:text-white transition font-medium"
            >
              Вписване
            </Link>
          )}
        </div>
      </div>
    </nav>
  );
}
