import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import "../AntivirusList.css";

function AntivirusListT() {
  const [products, setProducts] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    fetch("http://localhost:5000/api/admin")
      .then((res) => res.json())
      .then(async (data) => {
        const enriched = await Promise.all(
          data.map(async (product) => {
            try {
              const res = await fetch(
                `http://localhost:5000/api/products/${product.ProductName}/features`
              );
              const features = await res.json();
              return { ...product, features };
            } catch (err) {
              console.error("❌ Грешка при взимане на features:", err);
              return { ...product, features: [] };
            }
          })
        );
        setProducts(enriched);
      })
      .catch((err) => {
        console.error("❌ Грешка при зареждане на продукти:", err);
        setProducts([]);
      });
  }, []);

  return (
    <div className="antivirus-list-container">
      {products.map((product) => (
        <div
          key={product.ProductID}
          className="product-card"
          onClick={() => navigate(`/product/${product.ProductName}`)}
          style={{ cursor: "pointer" }}
        >
          <img
            src={product.ImageURL || "/placeholder.png"}
            alt={product.ProductName}
            className="product-img"
          />
          <div className="product-info">
            <div className="product-title">{product.ProductName}</div>
            <div className="product-description">
              {product.Description?.slice(0, 100)}...
            </div>
            <div className="product-price">{product.Price} лв</div>
          </div>
        </div>
      ))}
    </div>
  );
}

export default AntivirusListT;
