import React, { useState, useEffect } from "react";
import Navbar from "./components/Navbar";
import "./index.css";

function AdminPage() {
  const [products, setProducts] = useState([]);
  const [name, setName] = useState("");
  const [description, setDescription] = useState("");
  const [price, setPrice] = useState("");
  const [image, setImage] = useState("");

  useEffect(() => {
    fetchProducts();
  }, []);

  const fetchProducts = async () => {
    try {
      const res = await fetch("http://localhost:5000/api/products");
      const data = await res.json();
      setProducts(data);
    } catch (err) {
      console.error("Грешка при зареждане:", err);
    }
  };

  const convertToBase64 = (file) => {
    return new Promise((resolve, reject) => {
      const reader = new FileReader();
      reader.readAsDataURL(file);
      reader.onload = () => resolve(reader.result);
      reader.onerror = (error) => reject(error);
    });
  };

  const handleImageChange = async (e) => {
    const file = e.target.files[0];
    if (file) {
      const base64 = await convertToBase64(file);
      setImage(base64);
    }
  };

  const handleAdd = async () => {
    try {
      const res = await fetch("http://localhost:5000/api/products", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ name, description, price, image }),
      });
      const data = await res.json();
      alert(data.message || "Добавен продукт!");
      setName("");
      setDescription("");
      setPrice("");
      setImage("");
      fetchProducts();
    } catch (err) {
      console.error("Грешка при добавяне:", err);
    }
  };

  const handleDelete = async (id) => {
    try {
      const res = await fetch(`http://localhost:5000/api/products/${id}`, {
        method: "DELETE",
      });
      const data = await res.json();
      alert(data.message || "Изтрит продукт!");
      fetchProducts();
    } catch (err) {
      console.error("Грешка при изтриване:", err);
    }
  };

  return (
    <div className="admin-page">
      <Navbar />
      <div className="admin-form">
        <h2>Добавяне на продукт</h2>
        <input
          type="text"
          placeholder="Име"
          value={name}
          onChange={(e) => setName(e.target.value)}
        />
        <input
          type="text"
          placeholder="Описание"
          value={description}
          onChange={(e) => setDescription(e.target.value)}
        />
        <input
          type="number"
          placeholder="Цена"
          value={price}
          onChange={(e) => setPrice(e.target.value)}
        />
        <input type="file" accept="image/*" onChange={handleImageChange} />
        <button onClick={handleAdd}>Добави</button>
      </div>

      <div className="product-table">
        <table>
          <thead>
            <tr>
              <th>Снимка</th>
              <th>Име</th>
              <th>Описание</th>
              <th>Цена</th>
              <th>Действие</th>
            </tr>
          </thead>
          <tbody>
            {products.length === 0 ? (
              <tr>
                <td colSpan="5">Няма продукти.</td>
              </tr>
            ) : (
              products.map((p) => (
                <tr key={p.id}>
                  <td>
                    {p.image && (
                      <img src={p.image} alt={p.name} style={{ width: 50 }} />
                    )}
                  </td>
                  <td>{p.name}</td>
                  <td>{p.description}</td>
                  <td>{p.price} лв.</td>
                  <td>
                    <button
                      onClick={() => handleDelete(p.id)}
                      className="delete-button"
                    >
                      Изтрий
                    </button>
                  </td>
                </tr>
              ))
            )}
          </tbody>
        </table>
      </div>
    </div>
  );
}

export default AdminPage;
