import React, { useState, useEffect } from "react";
import { ChevronDown, ChevronUp } from "lucide-react";

function ExpertReviews() {
  const [products, setProducts] = useState([]);
  const [expandedId, setExpandedId] = useState(null);

  useEffect(() => {
    fetch("http://localhost:5000/api/admin")
      .then((res) => res.json())
      .then((data) => setProducts(data))
      .catch((err) => console.error("❌ Expert Review Fetch Error:", err));
  }, []);

  const toggleExpand = (id) => {
    setExpandedId(expandedId === id ? null : id);
  };

  const parseList = (text) => {
    if (typeof text === "string") {
      return text.split("\n").filter((line) => line.trim() !== "");
    }
    return Array.isArray(text) ? text : [];
  };

  return (
    <section className="py-16 bg-white" id="reviews">
      <div className="container mx-auto px-4">
        <div className="max-w-4xl mx-auto">
          <h2 className="text-3xl font-bold text-gray-900 mb-2 text-center">
            Expert Reviews
          </h2>
          <p className="text-gray-600 text-center mb-10 max-w-2xl mx-auto">
            Our in-depth analysis of each antivirus solution based on extensive testing.
          </p>

          <div className="space-y-6">
            {products.map((product) => (
              <div key={product.ProductID} className="border border-gray-200 rounded-lg shadow-sm">
                <button
                  className="w-full text-left p-4 bg-gray-50 flex justify-between items-center"
                  onClick={() => toggleExpand(product.ProductID)}
                >
                  <div className="flex items-center gap-4">
                    <img
                      src={product.ImageURL}
                      alt={product.ProductName}
                      className="w-12 h-12 object-contain rounded"
                    />
                    <div>
                      <h3 className="font-semibold text-lg">{product.ProductName}</h3>
                      <p className="text-yellow-500 text-sm">
                        ⭐ {product.AverageRating ? product.AverageRating.toFixed(1) : "N/A"} / 5.0
                      </p>
                    </div>
                  </div>
                  <span className="text-gray-600">
                    {expandedId === product.ProductID ? <ChevronUp /> : <ChevronDown />}
                  </span>
                </button>

                {expandedId === product.ProductID && (
                  <div className="p-6 space-y-6 bg-white text-gray-800 text-sm">
                    <div>
                      <h4 className="font-semibold mb-2">Our Review</h4>
                      <p className="whitespace-pre-line">{product.ReviewText || "No review available."}</p>
                    </div>

                    <div className="flex flex-col md:flex-row gap-6">
                      <div className="flex-1">
                        <h5 className="text-green-600 font-semibold mb-2">Pros</h5>
                        <ul className="list-disc ml-5 text-green-700 space-y-1">
                          {parseList(product.Pros).map((pro, idx) => (
                            <li key={idx}>{pro}</li>
                          ))}
                        </ul>
                      </div>
                      <div className="flex-1">
                        <h5 className="text-red-600 font-semibold mb-2">Cons</h5>
                        <ul className="list-disc ml-5 text-red-700 space-y-1">
                          {parseList(product.Cons).map((con, idx) => (
                            <li key={idx}>{con}</li>
                          ))}
                        </ul>
                      </div>
                    </div>

                    <div>
                      <h5 className="font-semibold mb-1">Bottom Line</h5>
                      <p>{product.BottomLine || "Summary not available."}</p>
                    </div>

                    <div>
                      <h5 className="font-semibold mb-2">Test Results</h5>
                      <ul className="text-sm space-y-1">
                        {(Array.isArray(product.TestResults) ? product.TestResults : []).map((result, i) => (
                          <li key={i} className="flex justify-between border-b pb-1">
                            <span>{result.label}</span>
                            <span className="font-semibold">{result.score}</span>
                          </li>
                        ))}
                      </ul>
                    </div>

                    <div>
                      <h5 className="font-semibold mb-2">Protection Features</h5>
                      <ul className="text-sm grid grid-cols-2 gap-x-4 gap-y-1">
                        {parseList(product.ProtectionFeatures).map((feature, i) => (
                          <li key={i} className="flex items-center gap-2">
                            <input type="checkbox" checked readOnly />
                            {feature}
                          </li>
                        ))}
                      </ul>
                    </div>

                    <div className="pt-4">
                      <a
                        href={product.OfficialWebsite}
                        target="_blank"
                        rel="noopener noreferrer"
                        className="inline-block bg-blue-600 hover:bg-blue-700 text-white px-4 py-2 rounded"
                      >
                        Visit Official Website
                      </a>
                    </div>
                  </div>
                )}
              </div>
            ))}
          </div>
        </div>
      </div>
    </section>
  );
}

export default ExpertReviews;
