import React, { useEffect, useState } from "react";
import "./AntivirusList.css";

function AntivirusListT() {
  const [products, setProducts] = useState([]);

  useEffect(() => {
    fetch("http://localhost:5000/api/admin/products")
      .then(res => res.json())
      .then(data => {
        if (Array.isArray(data)) setProducts(data);
        else setProducts([]);
      })
      .catch(() => setProducts([]));
  }, []);

  return React.createElement(
    "div",
    { className: "antivirus-list-container" },
    [
      React.createElement("h2", { className: "antivirus-list-title", key: "title" }, "Нашите Антивирусни Предложения"),
      ...products.map(product =>
        React.createElement(
          "div",
          { className: "product-card", key: product.ProductID },
          [
            React.createElement("img", {
              src: product.ImageURL,
              alt: product.ProductName,
              className: "product-img"
            }),
            React.createElement(
              "div",
              { className: "product-info" },
              [
                React.createElement("h3", { className: "product-title", key: "name" }, product.ProductName),
                React.createElement("p", { className: "product-description", key: "desc" }, product.Description),
                React.createElement("p", { className: "product-price", key: "price" }, product.Price + " лв."),
                React.createElement(
                  "button",
                  { className: "review-button", key: "btn" },
                  "Прочети ревю"
                )
              ]
            )
          ]
        )
      )
    ]
  );
}

export default AntivirusListT;
