import React, { useState, useEffect } from "react";
import Navbar from "./components/Navbar";
import "./index.css";

function AdminPageTemp() {
  const [products, setProducts] = useState([]);
  const [name, setName] = useState("");
  const [description, setDescription] = useState("");
  const [price, setPrice] = useState("");
  const [image, setImage] = useState("");
  const [fileName, setFileName] = useState("");
  const [editingId, setEditingId] = useState(null);

  useEffect(() => {
    fetchProducts();
  }, []);

  const fetchProducts = async () => {
    try {
      const res = await fetch("http://localhost:5000/api/admin/products");
      const data = await res.json();

      if (Array.isArray(data)) {
        setProducts(data);
      } else {
        console.warn("⚠️ Отговорът не е масив:", data);
        setProducts([]);
      }
    } catch (err) {
      console.error("❌ Грешка при зареждане:", err);
      setProducts([]);
    }
  };

  const convertToBase64 = (file) => {
    return new Promise((resolve, reject) => {
      const reader = new FileReader();
      reader.readAsDataURL(file);
      reader.onload = () => resolve(reader.result);
      reader.onerror = (error) => reject(error);
    });
  };

  const handleImageChange = async (e) => {
    const file = e.target.files[0];
    if (file) {
      const base64 = await convertToBase64(file);
      setImage(base64);
      setFileName(file.name);
    }
  };

  const handleAdd = async () => {
    if (!name || !description || !image || !price) {
      alert("Моля, попълнете всички полета.");
      return;
    }

    const method = editingId ? "PUT" : "POST";
    const url = editingId
      ? `http://localhost:5000/api/admin/products${editingId}`
      : "http://localhost:5000/api/admin/products";

    try {
      const res = await fetch(url, {
        method,
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ name, description, image, price }),
      });

      const data = await res.json();

      if (res.ok) {
        alert(data.message || (editingId ? "Продуктът е редактиран!" : "Добавен продукт!"));
        setName("");
        setDescription("");
        setPrice("");
        setImage("");
        setFileName("");
        setEditingId(null);
        fetchProducts();
      } else {
        alert(data.message || "Грешка");
        console.log("❌ SERVER ERROR:", data);
      }
    } catch (err) {
      console.error("❌ Грешка при заявката:", err);
      alert("⚠️ Сървърна грешка. Виж конзолата.");
    }
  };

  const handleDelete = async (id) => {
    try {
      const res = await fetch(`http://localhost:5000/api/admin${id}`, {
        method: "DELETE",
      });
      const data = await res.json();
      alert(data.message || "Изтрит продукт!");
      fetchProducts();
    } catch (err) {
      console.error("Грешка при изтриване:", err);
    }
  };

  return (
    <div className="admin-page">
      <Navbar />
      <div className="admin-form">
        <h2>Добавяне на продукт</h2>
        <input type="text" placeholder="Име" value={name} onChange={(e) => setName(e.target.value)} />
        <input type="text" placeholder="Описание" value={description} onChange={(e) => setDescription(e.target.value)} />
        <input type="text" placeholder="Цена (напр. xx.xx лв./месец)" value={price} onChange={(e) => setPrice(e.target.value)} />

        <label htmlFor="fileUpload" className="custom-file-upload">Изображение</label>
        <input id="fileUpload" type="file" accept="image/*" onChange={handleImageChange} style={{ display: "none" }} />
        {fileName && <p style={{ marginTop: "5px", fontSize: "14px" }}>Избран файл: {fileName}</p>}

        <button onClick={handleAdd}>{editingId ? "Запази промените" : "Добави"}</button>
      </div>

      <div className="product-table">
        <table>
          <thead>
            <tr>
              <th>Снимка</th>
              <th>Име</th>
              <th>Описание</th>
              <th>Цена</th>
              <th>Действие</th>
            </tr>
          </thead>
          <tbody>
            {Array.isArray(products) && products.length > 0 ? (
              products.map((p) => (
                <tr key={p.ProductID}>
                  <td>{p.ImageURL && <img src={p.ImageURL} alt={p.ProductName} style={{ width: 50 }} />}</td>
                  <td>{p.ProductName}</td>
                  <td>{p.Description}</td>
                  <td>{p.Price}</td>
                  <td style={{ display: "flex", gap: "0.5rem" }}>
                    <button
                      className="edit-button"
                      onClick={() => {
                        setName(p.ProductName);
                        setDescription(p.Description);
                        setPrice(p.Price);
                        setImage(p.ImageURL);
                        setEditingId(p.ProductID);
                      }}
                    >
                      Редактирай
                    </button>
                    <button onClick={() => handleDelete(p.ProductID)} className="delete-button">Изтрий</button>
                  </td>
                </tr>
              ))
            ) : (
              <tr>
                <td colSpan="5">Няма продукти.</td>
              </tr>
            )}
          </tbody>
        </table>
      </div>
    </div>
  );
}

export default AdminPageTemp;
