import React, { useEffect, useState } from "react";
import { useParams, useNavigate } from "react-router-dom";
import ReviewSection from "./components/ReviewSection";

function ProductDetails() {
  const { name } = useParams(); // –∏–∑–ø–æ–ª–∑–≤–∞–º–µ ProductName –æ—Ç URL
  const [product, setProduct] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
  fetch(`http://localhost:5000/api/admin${encodeURIComponent(name)}`)
    .then((res) => {
      if (!res.ok) throw new Error("–ü—Ä–æ–¥—É–∫—Ç—ä—Ç –Ω–µ –µ –Ω–∞–º–µ—Ä–µ–Ω");
      return res.json();
    })
    .then((data) => {
      console.log("üîç –î–∞–Ω–Ω–∏ –∑–∞ –ø—Ä–æ–¥—É–∫—Ç–∞:", data);
      setProduct(data);
    })
    .catch((err) => {
      console.error("‚ùå –ì—Ä–µ—à–∫–∞ –ø—Ä–∏ –∑–∞—Ä–µ–∂–¥–∞–Ω–µ:", err);
      navigate("/");
    });
}, [name, navigate]);


  if (!product) {
    return <div style={{ padding: "2rem", textAlign: "center" }}>–ó–∞—Ä–µ–∂–¥–∞–Ω–µ...</div>;
  }

  return (
    <div className="product-details" style={{ padding: "2rem" }}>
      <button onClick={() => navigate(-1)}>‚Üê –ù–∞–∑–∞–¥</button>
      <h2>{product.ProductName}</h2>
      <img src={product.ImageURL} alt={product.ProductName} style={{ height: "120px" }} />
      <p>{product.Description}</p>
      <p><strong>–¶–µ–Ω–∞:</strong> {product.Price}</p>

      {/* –†–µ–≤—é —Å–µ–∫—Ü–∏—è */}
      <ReviewSection productId={product.ProductID} />
    </div>
  );
}

export default ProductDetails;
